import React, { useState, useEffect } from "react";
import screenfull from "screenfull";

const OfficeScreen = ({
  officeScreenContent,
  handleDeleteItems,
  toggleLinkVisibility, // Receive toggle function
}) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
        toggleLinkVisibility(); // Call toggle function on fullscreen change
      };
      screenfull.on("change", changeHandler);
      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, [toggleLinkVisibility]);

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.key === "Escape" && screenfull.isFullscreen) {
        toggleLinkVisibility(); // Call toggle function on Escape key press in fullscreen mode
      }
    };

    document.addEventListener("keyup", handleKeyUp);

    return () => {
      document.removeEventListener("keyup", handleKeyUp);
    };
  }, [toggleLinkVisibility]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString("no-NO", options);
  };

  const renderEntry = (entry, index) => {
    let content;
    switch (entry.table) {
      case "events":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{entry.title}</span>
            <span style={{ textAlign: "right" }}>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "meetings":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>{entry.title}</span>
            <span style={{ textAlign: "center", flex: 1 }}>{entry.agenda}</span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "canteenMenu":
        const menu = Array.isArray(entry.menu)
          ? entry.menu.join(", ")
          : entry.menu;
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{menu}</span>
            <span style={{ textAlign: "right" }}>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "comments":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{entry.content}</span>
          </div>
        );
        break;
      default:
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{entry.comment}</span>
          </div>
        );
    }

    return (
      <div
        key={index}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(entry)}
            onChange={() => toggleSelection(entry)}
          />
        )}
        {content}
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    const index = selectedItems.indexOf(entry);
    if (index > -1) {
      setSelectedItems((prev) => prev.filter((item) => item !== entry));
    } else {
      setSelectedItems((prev) => [...prev, entry]);
    }
  };

  const handleDelete = () => {
    handleDeleteItems(selectedItems);
    setSelectedItems([]);
    toggleEditMode(); // Reset edit mode after deleting selected items
  };

  // Styles
  const containerStyle = {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "70px",
    padding: "20px",
  };

  const sectionStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)", // Adjusted background color with 30% opacity
    border: "1px solid #dee2e6",
    borderRadius: "5px",
    padding: "20px",
    marginBottom: "20px",
    width: "500px", // Fixed width
    height: "250px", // Fixed height
    overflow: "auto", // Makes content scrollable within the box
    boxShadow: "0px 5px 10px rgba(0, 0, 0, 0.1)", // Drop shadow definition
    textAlign: "center",
  };

  const titleStyle = {
    textAlign: "center", // Center the titles
    marginBottom: "30px",
    marginTop: "0px",
  };

  const subtitleStyle = {
    display: "flex",
    justifyContent: "space-between",
    padding: "0 20px",
    fontWeight: "bold",
  };

  return (
    <div>
      {showButton && (
        <div
          className="fullscreen-button-container"
          style={{ marginBottom: "20px", textAlign: "center" }}
        >
          <button onClick={toggleFullscreen}>
            {screenfull.isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          </button>
          {!screenfull.isFullscreen && (
            <button onClick={toggleEditMode}>
              {editMode ? "Cancel Edit" : "Edit"}
            </button>
          )}
        </div>
      )}

      <div style={containerStyle}>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Events</h3>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map((event, index) => renderEntry(event, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Meetings</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left" }}>Tittel</span>
            <span style={{ flex: 1, textAlign: "center" }}>Agenda</span>
            <span style={{ flex: 1, textAlign: "right" }}>Dato</span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map((meeting, index) => renderEntry(meeting, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Canteen Menu</h3>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map((menu, index) => renderEntry(menu, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Comments</h3>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "comments")
              .map((comment, index) => renderEntry(comment, index))}
          </ul>
        </div>
      </div>

      {editMode && (
        <button onClick={handleDelete} style={{ marginTop: "20px" }}>
          Remove Selected
        </button>
      )}
    </div>
  );
};

export default OfficeScreen;
