import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ViewContent = ({ setOfficeScreenContent }) => {
  const [selectedTable, setSelectedTable] = useState("");
  const [tableData, setTableData] = useState([]);
  const [selectedEntries, setSelectedEntries] = useState([]);
  const [showButtons, setShowButtons] = useState(false); // State to control button visibility
  const navigate = useNavigate();
  const selectRef = useRef(null); // Ref for select element

  const fetchTableData = async (table) => {
    try {
      const response = await axios.get(`http://localhost:3000/${table}`);
      setTableData(response.data);
    } catch (error) {
      console.error(`Error fetching data from ${table}:`, error);
    }
  };

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
    setShowButtons(false); // Hide buttons when table changes
    if (e.target.value) {
      fetchTableData(e.target.value);
    } else {
      setTableData([]);
    }
  };

  const handleCheckboxChange = (entry) => {
    setSelectedEntries((prevEntries) => {
      if (prevEntries.includes(entry)) {
        return prevEntries.filter((e) => e !== entry);
      } else {
        return [...prevEntries, entry];
      }
    });
  };

  const formatDate = (dateString) => {
    const options = {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const handleAddToScreen = async () => {
    const promises = selectedEntries.map((entry) => {
      return axios.get(`http://localhost:3000/${selectedTable}/${entry.id}`);
    });

    try {
      const results = await Promise.all(promises);
      const entriesData = results.map((result) => ({
        ...result.data,
        table: selectedTable,
      }));

      // Log the entries being added
      console.log("Entries Data:", entriesData);

      // Merge new data with existing data from localStorage
      setOfficeScreenContent((prevContent) => {
        const savedContent = localStorage.getItem("officeScreenContent");
        const parsedContent = savedContent ? JSON.parse(savedContent) : [];
        const newContent = [...parsedContent, ...entriesData];
        console.log("New OfficeScreenContent:", newContent);
        return newContent;
      });

      navigate("/office-screen");
    } catch (error) {
      console.error("Error fetching data for selected entries:", error);
    }
  };

  const handleDeleteSelected = async () => {
    const promises = selectedEntries.map((entry) => {
      return axios.delete(`http://localhost:3000/${selectedTable}/${entry.id}`);
    });

    try {
      await Promise.all(promises);
      alert("Selected entries have been successfully deleted.");
      fetchTableData(selectedTable); // Refetch data to update UI
      setSelectedEntries([]); // Clear selection after deletion
    } catch (error) {
      console.error("Error deleting selected entries:", error);
    }
  };

  useEffect(() => {
    // Show buttons only when a table is selected
    setShowButtons(selectedTable !== "");

    // Adjust the width of the select element based on the longest option text
    const longestOptionTextWidth = Math.max(
      ...Array.from(selectRef.current.options).map(
        (option) => option.text.length * 8 // Adjust the multiplier as needed
      )
    );
    selectRef.current.style.width = `${longestOptionTextWidth + 40}px`; // Add some padding
  }, [selectedTable]);

  const buttonStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)",
    color: "black",
    border: "none",
    borderRadius: "4px",
    padding: "10px 20px",
    cursor: "pointer",
    fontWeight: "bold",
    marginTop: "20px",
    marginRight: "10px",
    transition: "background-color 0.3s, color 0.3s",
  };

  return (
    <div>
      <h1>View and Select Content</h1>
      <label htmlFor="table-select"></label>
      <select
        id="table-select"
        ref={selectRef} // Assign ref to select element
        value={selectedTable}
        onChange={handleTableChange}
      >
        <option value="">Select...</option>
        <option value="users">Users</option>
        <option value="comments">Comments</option>
        <option value="events">Events</option>
        <option value="canteenMenu">Canteen Menu</option>
        <option value="meetings">Meetings</option>
      </select>
      <div>
        <h2 style={{ marginBottom: "20px" }}>Content from {selectedTable}</h2>
        {tableData.length > 0 && (
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              textAlign: "left",
            }}
          >
            <thead>
              <tr>
                <th>Select</th>
                {Object.keys(tableData[0]).map((key) => (
                  <th key={key}>
                    {key.charAt(0).toUpperCase() + key.slice(1)}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {tableData.map((entry, index) => (
                <tr
                  key={index}
                  style={{
                    backgroundColor:
                      index % 2 === 0
                        ? "rgba(227, 213, 202, 0.2)"
                        : "transparent",
                  }}
                >
                  <td>
                    <input
                      type="checkbox"
                      id={`checkbox-${index}`}
                      name={`checkbox-${index}`}
                      checked={selectedEntries.includes(entry)}
                      onChange={() => handleCheckboxChange(entry)}
                    />
                  </td>
                  {Object.keys(tableData[0]).map((key) => (
                    <td key={key}>
                      {entry[key] !== undefined
                        ? key === "date" || key === "birthdate"
                          ? formatDate(entry[key])
                          : entry[key]
                        : "N/A"}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
      {showButtons && (
        <>
          <button
            onClick={handleAddToScreen}
            style={{
              ...buttonStyle,
              backgroundColor: "rgba(227, 213, 202, 0.8)", // Darken background color on hover
              color: "black", // Change text color to black on hover
            }}
          >
            Add to Screen
          </button>
          <button
            onClick={handleDeleteSelected}
            style={{
              ...buttonStyle,
              backgroundColor: "rgba(227, 213, 202, 0.8)", // Darken background color on hover
              color: "black", // Change text color to black on hover
            }}
          >
            Delete Selected
          </button>
        </>
      )}
    </div>
  );
};

export default ViewContent;
