import React, { useState } from "react";
import axios from "axios";
import "./DynamicForm.css"; // Import the CSS file

const DynamicForm = () => {
  const [selectedType, setSelectedType] = useState("");
  const [formData, setFormData] = useState({});
  const [successMessage, setSuccessMessage] = useState(""); // State for success message

  const handleTypeChange = (e) => {
    setSelectedType(e.target.value);
    setFormData({});
    setSuccessMessage(""); // Clear the message when type changes
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let endpoint;
      switch (selectedType) {
        case "user":
          endpoint = "/users";
          break;
        case "comment":
          endpoint = "/comments";
          break;
        case "event":
          endpoint = "/events"; // Ensure this matches the backend route
          break;
        case "canteen_menu":
          endpoint = "/canteenMenu";
          break;
        case "meeting":
          endpoint = "/meetings";
          break;
        default:
          return;
      }
      const response = await axios.post(
        `http://localhost:3000${endpoint}`,
        formData
      );
      console.log("Response data:", response.data); // Log the response data to check its structure

      if (selectedType === "user") {
        setSuccessMessage(
          `Successfully added! User ID: ${response.data.userId}`
        );
      } else {
        setSuccessMessage("Successfully added!");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      setSuccessMessage("Failed to add.");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Select type to add:
          <select value={selectedType} onChange={handleTypeChange}>
            <option value="">Select...</option>
            <option value="user">User</option>
            <option value="comment">Comment</option>
            <option value="event">Event</option>
            <option value="canteen_menu">Canteen Menu</option>
            <option value="meeting">Meeting</option>
          </select>
        </label>
        {selectedType && (
          <div>
            {selectedType === "user" && (
              <>
                <label>
                  Name:
                  <input
                    type="text"
                    name="name"
                    value={formData.name || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Email:
                  <input
                    type="email"
                    name="email"
                    value={formData.email || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Birthdate:
                  <input
                    type="date"
                    name="birthdate"
                    value={formData.birthdate || ""}
                    onChange={handleInputChange}
                  />
                </label>
              </>
            )}
            {selectedType === "comment" && (
              <>
                <label>
                  Content:
                  <input
                    type="text"
                    name="content"
                    value={formData.content || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  User ID:
                  <input
                    type="number"
                    name="user_id"
                    value={formData.user_id || ""}
                    onChange={handleInputChange}
                  />
                </label>
              </>
            )}
            {selectedType === "event" && (
              <>
                <label>
                  Title:
                  <input
                    type="text"
                    name="title"
                    value={formData.title || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Date:
                  <input
                    type="date"
                    name="date"
                    value={formData.date || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Description:
                  <input
                    type="text"
                    name="description"
                    value={formData.description || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Created By (User ID):
                  <input
                    type="number"
                    name="created_by"
                    value={formData.created_by || ""}
                    onChange={handleInputChange}
                  />
                </label>
              </>
            )}
            {selectedType === "canteen_menu" && (
              <>
                <label>
                  Date:
                  <input
                    type="date"
                    name="date"
                    value={formData.date || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Menu:
                  <input
                    type="text"
                    name="menu"
                    value={formData.menu || ""}
                    onChange={handleInputChange}
                  />
                </label>
              </>
            )}
            {selectedType === "meeting" && (
              <>
                <label>
                  Title:
                  <input
                    type="text"
                    name="title"
                    value={formData.title || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Date:
                  <input
                    type="date"
                    name="date"
                    value={formData.date || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Description:
                  <input
                    type="text"
                    name="description"
                    value={formData.description || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Agenda:
                  <input
                    type="text"
                    name="agenda"
                    value={formData.agenda || ""}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Created By (User ID):
                  <input
                    type="number"
                    name="created_by"
                    value={formData.created_by || ""}
                    onChange={handleInputChange}
                  />
                </label>
              </>
            )}
            <button type="submit">Add {selectedType}</button>
          </div>
        )}
      </form>
      {successMessage && (
        <div className="success-message">
          <span>&#10003;</span> {successMessage}
        </div>
      )}
    </div>
  );
};

export default DynamicForm;
