import React from "react";
import "bootstrap/dist/css/bootstrap.min.css"; // Ensure Bootstrap CSS is imported

const OfficeScreen = ({ officeScreenContent }) => {
  return (
    <div className="container mt-4">
      <div className="row">
        {/* Events Section */}
        <div className="col-md-6">
          <div className="p-3 mb-3 bg-light border">
            <h4>Events</h4>
            <ul>
              {officeScreenContent
                .filter((entry) => entry.table === "events")
                .map((event, index) => (
                  <li key={index}>{event.description}</li> // Adjust according to your data structure
                ))}
            </ul>
          </div>
        </div>

        {/* Comments Section */}
        <div className="col-md-6">
          <div className="p-3 mb-3 bg-light border">
            <h4>Comments</h4>
            <ul>
              {officeScreenContent
                .filter((entry) => entry.table === "comments")
                .map((comment, index) => (
                  <li key={index}>{comment.text}</li> // Adjust according to your data structure
                ))}
            </ul>
          </div>
        </div>

        {/* Meetings Section */}
        <div className="col-md-6">
          <div className="p-3 mb-3 bg-light border">
            <h4>Meetings</h4>
            <ul>
              {officeScreenContent
                .filter((entry) => entry.table === "meetings")
                .map((meeting, index) => (
                  <li key={index}>{meeting.topic}</li> // Adjust according to your data structure
                ))}
            </ul>
          </div>
        </div>

        {/* Canteen Menu Section */}
        <div className="col-md-6">
          <div className="p-3 mb-3 bg-light border">
            <h4>Canteen Menu</h4>
            <ul>
              {officeScreenContent
                .filter((entry) => entry.table === "canteenMenu")
                .map((menu, index) => (
                  <li key={index}>{menu.dish}</li> // Adjust according to your data structure
                ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OfficeScreen;
