import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";
import axios from "axios";

const App = () => {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);
  const [userData, setUserData] = useState([]);
  const [eventData, setEventData] = useState([]);
  const [canteenMenuData, setCanteenMenuData] = useState([]);
  const [commentData, setCommentData] = useState([]);
  const [meetingData, setMeetingData] = useState([]);

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedContent = localStorage.getItem("officeScreenContent");
    if (savedContent) {
      // Parse the JSON string back into an array of objects
      setOfficeScreenContent(JSON.parse(savedContent));

      // Debugging: Log the loaded content
      console.log("Data loaded from localStorage:", JSON.parse(savedContent));
    } else {
      // Debugging: Log if no data was found
      console.log("No data found in localStorage.");
    }

    // Fetch data for the home page
    const fetchData = async () => {
      try {
        const userResponse = await axios.get("http://your-api-url/users");
        setUserData(userResponse.data);

        const eventResponse = await axios.get("http://your-api-url/events");
        setEventData(eventResponse.data);

        const canteenMenuResponse = await axios.get(
          "http://your-api-url/canteenMenu"
        );
        setCanteenMenuData(canteenMenuResponse.data);

        const commentResponse = await axios.get("http://your-api-url/comments");
        setCommentData(commentResponse.data);

        const meetingResponse = await axios.get("http://your-api-url/meetings");
        setMeetingData(meetingResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Save data to localStorage whenever officeScreenContent changes
  useEffect(() => {
    // Debugging: Log before saving to verify data structure
    console.log("Saving data to localStorage:", officeScreenContent);

    // Stringify the data before saving
    localStorage.setItem(
      "officeScreenContent",
      JSON.stringify(officeScreenContent)
    );
  }, [officeScreenContent]); // This useEffect will trigger when the content changes

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/add">Add Data</Link>
            </li>
            <li>
              <Link to="/view">View Content</Link>
            </li>
            <li>
              <Link to="/office-screen">Office Screen</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div>
                <h1>Welcome to the Digital Signage System</h1>
                {/* Render home page content */}
                <div>
                  <h2>Users</h2>
                  <ul>
                    {userData.map((user, index) => (
                      <li key={index}>{user.name}</li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h2>Events</h2>
                  <ul>
                    {eventData.map((event, index) => (
                      <li key={index}>{event.title}</li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h2>Canteen Menu</h2>
                  <ul>
                    {canteenMenuData.map((menuItem, index) => (
                      <li key={index}>{menuItem.name}</li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h2>Comments</h2>
                  <ul>
                    {commentData.map((comment, index) => (
                      <li key={index}>{comment.text}</li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h2>Meetings</h2>
                  <ul>
                    {meetingData.map((meeting, index) => (
                      <li key={index}>{meeting.title}</li>
                    ))}
                  </ul>
                </div>
              </div>
            }
          />
          <Route path="/add" element={<DynamicForm />} />
          <Route
            path="/view"
            element={
              <ViewContent setOfficeScreenContent={setOfficeScreenContent} />
            }
          />
          <Route
            path="/office-screen"
            element={<OfficeScreen officeScreenContent={officeScreenContent} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
