import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ViewContent = ({ setOfficeScreenContent }) => {
  const [selectedTable, setSelectedTable] = useState("");
  const [tableData, setTableData] = useState([]);
  const [selectedEntries, setSelectedEntries] = useState([]);

  const fetchTableData = async (table) => {
    try {
      const response = await axios.get(`http://localhost:3000/${table}`);
      setTableData(response.data);
    } catch (error) {
      console.error(`Error fetching data from ${table}:`, error);
    }
  };

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
    if (e.target.value) {
      fetchTableData(e.target.value);
    } else {
      setTableData([]);
    }
  };

  const handleCheckboxChange = (entry) => {
    setSelectedEntries((prevEntries) => {
      if (prevEntries.includes(entry)) {
        return prevEntries.filter((e) => e !== entry);
      } else {
        return [...prevEntries, entry];
      }
    });
  };

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry) => {
    return (
      <div>
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  const handleAddToScreen = async () => {
    const promises = selectedEntries.map(async (entry) => {
      const response = await axios.get(
        `http://localhost:3000/${selectedTable}/${entry.id}`
      );
      return response.data;
    });

    const entriesData = await Promise.all(promises);
    setOfficeScreenContent((prevContent) => [...prevContent, ...entriesData]);
  };

  return (
    <div>
      <h1>View and Select Content</h1>
      <label>
        Select Table:
        <select value={selectedTable} onChange={handleTableChange}>
          <option value="">Select...</option>
          <option value="users">Users</option>
          <option value="comments">Comments</option>
          <option value="events">Events</option>
          <option value="canteenMenu">Canteen Menu</option>
          <option value="meetings">Meetings</option>
        </select>
      </label>
      <div>
        <h2>Content from {selectedTable}</h2>
        {tableData.length > 0 && (
          <ul>
            {tableData.map((entry, index) => (
              <li key={index}>
                <input
                  type="checkbox"
                  checked={selectedEntries.includes(entry)}
                  onChange={() => handleCheckboxChange(entry)}
                />
                {renderEntry(entry)}
              </li>
            ))}
          </ul>
        )}
      </div>
      <button onClick={handleAddToScreen}>Add to Screen</button>
    </div>
  );
};

export default ViewContent;
