// Home.js
import React from "react";
import homeImage from "./images/home-image.png"; // Import the image

const Home = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "flex-start",
        height: "80vh",
        paddingLeft: "20px",
        paddingRight: "20px",
        marginTop: "100px", // Add top margin here
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "flex-start",
          marginRight: "20px",
        }}
      >
        <h1
          style={{
            fontSize: "40px",
            fontWeight: "bold",
            color: "black",
            marginBottom: "20px",
            marginTop: "0",
          }}
        >
          Enkel og effektiv informasjonvisning
        </h1>

        <h2
          style={{
            fontSize: "20px",
            fontWeight: "normal",
            color: "gray",
            marginTop: "0",
          }}
        >
          Sanntidsinformasjon på jobben. Tilpass innholdet med noen få enkle
          trinn.
        </h2>
      </div>

      <img
        src={homeImage}
        alt="Home"
        style={{
          width: "500px",
          height: "auto",
          boxShadow: "0px 5px 10px rgba(0, 0, 0, 0.5)", // Drop shadow definition
        }}
      />
    </div>
  );
};

export default Home;
