import React, { useEffect, useState } from "react";

// List of bus stops with their IDs
const busStops = [
  { name: "Nyjordet", id: "NSR:StopPlace:5738" },
  { name: "Bjørnholt skole", id: "NSR:StopPlace:5751" },
  { name: "Bjørnholt nedre", id: "NSR:StopPlace:5731" },
  { name: "Oredalen", id: "NSR:StopPlace:5702" },
];

const Entur = () => {
  const [currentStopIndex, setCurrentStopIndex] = useState(0);
  const [departures, setDepartures] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDepartures = async () => {
      const query = `
        {
          stopPlace(id: "${busStops[currentStopIndex].id}") {
            name
            estimatedCalls(timeRange: 72100, numberOfDepartures: 20, whiteListedModes: [bus]) {
              realtime
              aimedArrivalTime
              expectedArrivalTime
              destinationDisplay {
                frontText
              }
              serviceJourney {
                journeyPattern {
                  line {
                    publicCode
                    name
                  }
                }
              }
            }
          }
        }
      `;

      try {
        console.log("Fetching data...");
        const response = await fetch(
          "https://api.entur.io/journey-planner/v2/graphql",
          {
            method: "POST",
            headers: {
              "ET-Client-Name": "your-app-name", // Replace with your app name
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ query }),
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        console.log("Fetch result:", result);

        if (result.errors) {
          throw new Error(
            `GraphQL error! ${result.errors.map((e) => e.message).join(", ")}`
          );
        }

        if (!result.data || !result.data.stopPlace) {
          throw new Error("No stopPlace data found in the response.");
        }

        const filteredDepartures = result.data.stopPlace.estimatedCalls.filter(
          (call) =>
            ["71", "72", "77"].includes(
              call.serviceJourney.journeyPattern.line.publicCode
            ) &&
            !["Bjørndal", "Seterbråten"].includes(
              call.destinationDisplay.frontText
            )
        );

        setDepartures(filteredDepartures);
      } catch (err) {
        setError(err.message);
        console.error("Fetching error: ", err);
      }
    };

    fetchDepartures();
    const interval = setInterval(fetchDepartures, 60000); // Fetch data every 60 seconds

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, [currentStopIndex]);

  const formatTimeDifference = (time) => {
    const now = new Date();
    const departureTime = new Date(time);
    const diffMs = departureTime - now;
    const diffMins = Math.round(diffMs / 60000);

    if (diffMins <= 0) {
      return "Nå";
    } else if (diffMins > 14) {
      return departureTime.toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      });
    }

    return `${diffMins} min`;
  };

  const closestDepartures = departures.slice(0, 4); // Display 4 closest departures
  const upcomingDepartures = departures.slice(4);

  const handlePrevStop = () => {
    setCurrentStopIndex((prevIndex) =>
      prevIndex > 0 ? prevIndex - 1 : busStops.length - 1
    );
  };

  const handleNextStop = () => {
    setCurrentStopIndex((prevIndex) =>
      prevIndex < busStops.length - 1 ? prevIndex + 1 : 0
    );
  };

  return (
    <div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div className="header">
        <button
          onClick={handlePrevStop}
          style={{ background: "none", border: "none" }}
        >
          <img
            src="/path/to/left-arrow-icon.png"
            alt="Left Arrow"
            style={{ height: "30px", cursor: "pointer" }}
          />
        </button>
        <h1>{busStops[currentStopIndex].name}</h1>
        <button
          onClick={handleNextStop}
          style={{ background: "none", border: "none" }}
        >
          <img
            src="/path/to/right-arrow-icon.png"
            alt="Right Arrow"
            style={{ height: "30px", cursor: "pointer" }}
          />
        </button>
      </div>
      <div className="content-text" style={{ marginBottom: "20px" }}>
        {closestDepartures.map((departure, index) => (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "10px", // Add space between the items
              padding: "10px",
              backgroundColor: "#242f45",
              color: "white",
              borderRadius: "5px",
              fontWeight: "bold",
            }}
          >
            <div style={{ textAlign: "left" }}>
              {departure.serviceJourney.journeyPattern.line.publicCode}{" "}
              {departure.destinationDisplay.frontText}:
            </div>
            <div style={{ textAlign: "right" }}>
              {formatTimeDifference(departure.expectedArrivalTime)}
            </div>
          </div>
        ))}
      </div>
      <div>
        <div
          style={{
            whiteSpace: "nowrap",
            overflow: "hidden",
            boxSizing: "border-box",
          }}
        >
          <div
            style={{
              display: "inline-block",
              paddingLeft: "100%",
              animation: "marquee 30s linear infinite", // Adjust the duration to slow down the speed
            }}
          >
            {upcomingDepartures.map((departure, index) => (
              <span
                key={index}
                style={{
                  marginRight: "20px",
                  padding: "5px",
                  backgroundColor: "#242f45",
                  color: "white",
                  borderRadius: "5px",
                }}
              >
                {departure.serviceJourney.journeyPattern.line.publicCode}{" "}
                {departure.destinationDisplay.frontText}:{" "}
                {formatTimeDifference(departure.expectedArrivalTime)}
              </span>
            ))}
          </div>
        </div>
      </div>
      <style>
        {`
          @keyframes marquee {
            0% {
              transform: translate(0, 0);
            }
            100% {
              transform: translate(-100%, 0);
            }
          }
          @media (min-width: 1420px) {
            .content-text {
              font-size: 1.5em;
            }
          }
          .header {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 20px;
            margin-bottom: 20px;
          }
          .header button img {
            filter: invert(100%); /* Invert colors for white arrows */
          }
        `}
      </style>
    </div>
  );
};

export default Entur;
