import React, { useState, useEffect } from "react";
import screenfull from "screenfull";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashAlt, faEdit, faTimes } from "@fortawesome/free-solid-svg-icons";
import Entur from "./Entur"; // Import Entur component
import "./Office.css";
import "./index.css";

const OfficeScreen = ({
  officeScreenContent,
  handleDeleteItems,
  toggleLinkVisibility,
}) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);
  const [users, setUsers] = useState({});
  const [departures, setDepartures] = useState([]); // State for departures

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
        toggleLinkVisibility();
      };
      screenfull.on("change", changeHandler);
      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, [toggleLinkVisibility]);

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.key === "Escape" && screenfull.isFullscreen) {
        toggleLinkVisibility();
      }
    };

    document.addEventListener("keyup", handleKeyUp);

    return () => {
      document.removeEventListener("keyup", handleKeyUp);
    };
  }, [toggleLinkVisibility]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`);
      return response.data.name;
    } catch (error) {
      console.error(`Error fetching user data for user_id ${userId}:`, error);
      return "Unknown User";
    }
  };

  const fetchUsers = async () => {
    const uniqueUserIds = [
      ...new Set(
        officeScreenContent
          .filter((entry) => entry.table === "comments")
          .map((entry) => entry.user_id)
      ),
    ];
    const userData = {};
    for (const userId of uniqueUserIds) {
      userData[userId] = await fetchUserData(userId);
    }
    setUsers(userData);
  };

  useEffect(() => {
    fetchUsers();
  }, [officeScreenContent]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "short", day: "numeric" };
    return new Date(dateString).toLocaleDateString("no-NO", options);
  };

  const renderEntry = (entry, index) => {
    let content;
    switch (entry.table) {
      case "events":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>
              {entry.title || "No Title"}
            </span>
            <span style={{ textAlign: "center" }}>
              {entry.description || "No Description"}
            </span>
            <span style={{ textAlign: "right" }}>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "canteenMenu":
        const menu = Array.isArray(entry.menu)
          ? entry.menu.join(", ")
          : entry.menu;
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {menu || "No Menu"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "comments":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {entry.content || "No Comment"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {users[entry.user_id] || "Unknown User"}
            </span>
          </div>
        );
        break;
      default:
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{entry.comment}</span>
          </div>
        );
    }

    return (
      <div
        key={index}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(entry)}
            onChange={() => toggleSelection(entry)}
          />
        )}
        {content}
      </div>
    );
  };

  const renderDeparture = (departure, index) => {
    return (
      <div
        key={index}
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          width: "100%",
          padding: "20px",
        }}
      >
        <span style={{ textAlign: "left", flex: 1, color: "black" }}>
          {departure.serviceJourney.journeyPattern.line.name || "No Title"}
        </span>
        <span style={{ textAlign: "center", flex: 1, color: "black" }}>
          {departure.destinationDisplay.frontText || "No Destination"}
        </span>
        <span style={{ textAlign: "right", flex: 1, color: "black" }}>
          {new Date(departure.expectedArrivalTime).toLocaleTimeString()}
        </span>
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    setSelectedItems((prevSelected) => {
      if (prevSelected.includes(entry)) {
        return prevSelected.filter((item) => item !== entry);
      } else {
        return [...prevSelected, entry];
      }
    });
  };

  const handleDelete = () => {
    handleDeleteItems(selectedItems);
    setEditMode(false); // Toggle back to normal mode
    setSelectedItems([]); // Clear selected items
  };

  // Retrieve the departures and render them
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://api.entur.io/realtime/v1/rest/estimated-at-stop/NSR:StopPlace:4790?includeDepartures=true&limit=10"
        );
        setDepartures(response.data.estimatedCalls);
      } catch (error) {
        console.error("Error fetching departures:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div style={{ paddingTop: "80px" }}>
      {" "}
      {/* Adjusted padding to account for navbar height */}
      <button
        onClick={toggleFullscreen}
        style={{ position: "fixed", top: 0, right: 0, zIndex: 999 }}
      >
        {showButton ? (
          <FontAwesomeIcon icon={faTimes} />
        ) : (
          <FontAwesomeIcon icon={faTrashAlt} />
        )}
      </button>
      <button
        onClick={toggleEditMode}
        style={{
          position: "fixed",
          top: 0,
          right: "50px",
          zIndex: 999,
          visibility: editMode ? "hidden" : "visible",
        }}
      >
        <FontAwesomeIcon icon={faEdit} />
      </button>
      <div className="office-screen">
        <h1>Kontorskjerm</h1>
        <div>
          <h2>Events</h2>
          <div className="office-container">
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map(renderEntry)}
          </div>
        </div>
        <div>
          <h2>Canteen Menu</h2>
          <div className="office-container">
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map(renderEntry)}
          </div>
        </div>
        <div>
          <h2>Comments</h2>
          <div className="office-container">
            {officeScreenContent
              .filter((entry) => entry.table === "comments")
              .map(renderEntry)}
          </div>
        </div>
        {departures.length > 0 && (
          <div>
            <h2>Departures</h2>
            <div className="office-container">
              {departures.map(renderDeparture)}
            </div>
          </div>
        )}
        {editMode && (
          <div style={{ marginTop: "20px" }}>
            <button onClick={handleDelete}>Delete Selected</button>
          </div>
        )}
      </div>
      <Entur /> {/* Render the Entur component */}
    </div>
  );
};

export default OfficeScreen;
