import React, { useState } from "react";
import axios from "axios";

const DynamicForm = () => {
  const [selectedType, setSelectedType] = useState("");
  const [formData, setFormData] = useState({});
  const [successMessage, setSuccessMessage] = useState(""); // State for success message

  const handleTypeChange = (e) => {
    setSelectedType(e.target.value);
    setFormData({});
    setSuccessMessage(""); // Clear the message when type changes
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let endpoint;
      switch (selectedType) {
        case "user":
          endpoint = "/users";
          break;
        case "comment":
          endpoint = "/comments";
          break;
        case "event":
          endpoint = "/events"; // Ensure this matches the backend route
          break;
        case "canteen_menu":
          endpoint = "/canteenMenu";
          break;
        case "meeting":
          endpoint = "/meetings";
          break;
        default:
          return;
      }
      const response = await axios.post(
        `http://localhost:3000${endpoint}`,
        formData
      );
      console.log("Response data:", response.data); // Log the response data to check its structure

      if (selectedType === "user") {
        setSuccessMessage(
          `Successfully added! User ID: ${response.data.userId}`
        );
      } else {
        setSuccessMessage("Successfully added!");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      setSuccessMessage("Failed to add.");
    }
  };

  const containerStyle = {
    width: "300px",
    margin: "0",
  };

  const inputStyle = {
    display: "block",
    width: "100%",
    padding: "8px",
    margin: "10px 0",
    borderRadius: "4px",
    border: "1px solid #ccc",
    boxSizing: "border-box",
  };

  const buttonStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)",
    color: "black",
    border: "none",
    borderRadius: "4px",
    padding: "10px 20px",
    cursor: "pointer",
    fontWeight: "bold",
    marginTop: "20px",
  };

  return (
    <div style={containerStyle}>
      <form onSubmit={handleSubmit}>
        <label>
          Select type to add:
          <select
            value={selectedType}
            onChange={handleTypeChange}
            style={inputStyle}
          >
            <option value="">Select...</option>
            <option value="user">User</option>
            <option value="comment">Comment</option>
            <option value="event">Event</option>
            <option value="canteen_menu">Canteen Menu</option>
            <option value="meeting">Meeting</option>
          </select>
        </label>
        {selectedType && (
          <div>
            {selectedType === "user" && (
              <>
                <label>
                  Name:
                  <input
                    type="text"
                    name="name"
                    value={formData.name || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Email:
                  <input
                    type="email"
                    name="email"
                    value={formData.email || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Birthdate:
                  <input
                    type="date"
                    name="birthdate"
                    value={formData.birthdate || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
              </>
            )}
            {selectedType === "comment" && (
              <>
                <label>
                  Content:
                  <input
                    type="text"
                    name="content"
                    value={formData.content || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  User ID:
                  <input
                    type="number"
                    name="user_id"
                    value={formData.user_id || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
              </>
            )}
            {selectedType === "event" && (
              <>
                <label>
                  Title:
                  <input
                    type="text"
                    name="title"
                    value={formData.title || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Date:
                  <input
                    type="date"
                    name="date"
                    value={formData.date || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Description:
                  <input
                    type="text"
                    name="description"
                    value={formData.description || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Created By (User ID):
                  <input
                    type="number"
                    name="created_by"
                    value={formData.created_by || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
              </>
            )}
            {selectedType === "canteen_menu" && (
              <>
                <label>
                  Date:
                  <input
                    type="date"
                    name="date"
                    value={formData.date || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Menu:
                  <input
                    type="text"
                    name="menu"
                    value={formData.menu || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
              </>
            )}
            {selectedType === "meeting" && (
              <>
                <label>
                  Title:
                  <input
                    type="text"
                    name="title"
                    value={formData.title || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Date:
                  <input
                    type="date"
                    name="date"
                    value={formData.date || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Description:
                  <input
                    type="text"
                    name="description"
                    value={formData.description || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Agenda:
                  <input
                    type="text"
                    name="agenda"
                    value={formData.agenda || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
                <label>
                  Created By (User ID):
                  <input
                    type="number"
                    name="created_by"
                    value={formData.created_by || ""}
                    onChange={handleInputChange}
                    style={inputStyle}
                  />
                </label>
              </>
            )}
            <button type="submit" style={buttonStyle}>
              Add {selectedType}
            </button>
          </div>
        )}
      </form>
      {successMessage && (
        <div
          style={{
            display: "inline-flex",
            alignItems: "center",
            justifyContent: "center",
            backgroundColor: "rgba(227, 213, 202, 0.2)",
            color: "black", // Change font color to black
            border: "1px solid #c3e6cb",
            borderRadius: "4px",
            padding: "10px",
            marginTop: "20px",
            fontSize: "16px",
            fontWeight: "bold",
          }}
        >
          <span style={{ marginRight: "10px", fontSize: "20px" }}>
            &#10003;
          </span>
          {successMessage}
        </div>
      )}
    </div>
  );
};

export default DynamicForm;
