import React, { useState, useEffect } from "react";
import axios from "axios";

const ViewContent = () => {
  const [selectedTable, setSelectedTable] = useState("");
  const [tableData, setTableData] = useState([]);
  const [selectedEntries, setSelectedEntries] = useState({
    users: [],
    comments: [],
    events: [],
    canteenMenu: [],
    meetings: [],
  });

  const fetchTableData = async (table) => {
    try {
      const response = await axios.get(`http://localhost:3000/${table}`);
      setTableData(response.data);
    } catch (error) {
      console.error(`Error fetching data from ${table}:`, error);
    }
  };

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
    if (e.target.value) {
      fetchTableData(e.target.value);
    } else {
      setTableData([]);
    }
  };

  const handleCheckboxChange = (entry) => {
    setSelectedEntries((prevEntries) => {
      const updatedEntries = { ...prevEntries };
      const entryId = entry.id;
      if (updatedEntries[selectedTable].includes(entryId)) {
        updatedEntries[selectedTable] = updatedEntries[selectedTable].filter(
          (id) => id !== entryId
        );
      } else {
        updatedEntries[selectedTable].push(entryId);
      }
      return updatedEntries;
    });
  };

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry) => {
    return (
      <div>
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>View and Select Content</h1>
      <label>
        Select Table:
        <select value={selectedTable} onChange={handleTableChange}>
          <option value="">Select...</option>
          <option value="users">Users</option>
          <option value="comments">Comments</option>
          <option value="events">Events</option>
          <option value="canteenMenu">Canteen Menu</option>
          <option value="meetings">Meetings</option>
        </select>
      </label>
      <div>
        <h2>Content from {selectedTable}</h2>
        {tableData.length > 0 && (
          <ul>
            {tableData.map((entry) => (
              <li key={entry.id}>
                <input
                  type="checkbox"
                  checked={
                    selectedEntries[selectedTable]?.includes(entry.id) || false
                  }
                  onChange={() => handleCheckboxChange(entry)}
                />
                {renderEntry(entry)}
              </li>
            ))}
          </ul>
        )}
      </div>
      <div>
        <h2>Selected Entries for Signage</h2>
        <div className="signage-layout">
          <div className="signage-events">
            <h3>Events</h3>
            <ul>
              {selectedEntries.events.map((id) => {
                const event = tableData.find((entry) => entry.id === id);
                return <li key={id}>{renderEntry(event)}</li>;
              })}
            </ul>
          </div>
          <div className="signage-meetings">
            <h3>Meetings</h3>
            <ul>
              {selectedEntries.meetings.map((id) => {
                const meeting = tableData.find((entry) => entry.id === id);
                return <li key={id}>{renderEntry(meeting)}</li>;
              })}
            </ul>
          </div>
          <div className="signage-canteen-menu">
            <h3>Canteen Menu</h3>
            <ul>
              {selectedEntries.canteenMenu.map((id) => {
                const menu = tableData.find((entry) => entry.id === id);
                return <li key={id}>{renderEntry(menu)}</li>;
              })}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewContent;
