import React, { useState } from "react";
import axios from "axios";

const DynamicForm = ({ setOfficeScreenContent }) => {
  const [selectedType, setSelectedType] = useState("");
  const [formData, setFormData] = useState({});

  const handleTypeChange = (e) => {
    setSelectedType(e.target.value);
    setFormData({});
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let endpoint;
      switch (selectedType) {
        case "user":
          endpoint = "/users";
          break;
        case "comment":
          endpoint = "/comments";
          break;
        case "event":
          endpoint = "/events";
          break;
        case "canteen_menu":
          endpoint = "/canteenMenu";
          break;
        case "meeting":
          endpoint = "/meetings";
          break;
        default:
          return;
      }
      const response = await axios.post(
        `http://localhost:3000${endpoint}`,
        formData
      );
      console.log("Response:", response.data);
      alert("Successfully added!");

      // Update officeScreenContent state
      setOfficeScreenContent((prevContent) => [...prevContent, formData]);
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Failed to add.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Select type to add:
        <select value={selectedType} onChange={handleTypeChange}>
          <option value="">Select...</option>
          <option value="user">User</option>
          <option value="comment">Comment</option>
          <option value="event">Event</option>
          <option value="canteen_menu">Canteen Menu</option>
          <option value="meeting">Meeting</option>
        </select>
      </label>
      {selectedType && (
        <div>
          {selectedType === "user" && (
            <>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  value={formData.name || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Email:
                <input
                  type="email"
                  name="email"
                  value={formData.email || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Birthdate:
                <input
                  type="date"
                  name="birthdate"
                  value={formData.birthdate || ""}
                  onChange={handleInputChange}
                />
              </label>
            </>
          )}
          {selectedType === "comment" && (
            <>
              <label>
                Content:
                <input
                  type="text"
                  name="content"
                  value={formData.content || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                User ID:
                <input
                  type="number"
                  name="user_id"
                  value={formData.user_id || ""}
                  onChange={handleInputChange}
                />
              </label>
            </>
          )}
          {selectedType === "event" && (
            <>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  value={formData.title || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Date:
                <input
                  type="date"
                  name="date"
                  value={formData.date || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Description:
                <input
                  type="text"
                  name="description"
                  value={formData.description || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Created By (User ID):
                <input
                  type="number"
                  name="created_by"
                  value={formData.created_by || ""}
                  onChange={handleInputChange}
                />
              </label>
            </>
          )}
          {selectedType === "canteen_menu" && (
            <>
              <label>
                Date:
                <input
                  type="date"
                  name="date"
                  value={formData.date || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Menu:
                <input
                  type="text"
                  name="menu"
                  value={formData.menu || ""}
                  onChange={handleInputChange}
                />
              </label>
            </>
          )}
          {selectedType === "meeting" && (
            <>
              <label>
                Title:
                <input
                  type="text"
                  name="title"
                  value={formData.title || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Date:
                <input
                  type="date"
                  name="date"
                  value={formData.date || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Description:
                <input
                  type="text"
                  name="description"
                  value={formData.description || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Agenda:
                <input
                  type="text"
                  name="agenda"
                  value={formData.agenda || ""}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Created By (User ID):
                <input
                  type="number"
                  name="created_by"
                  value={formData.created_by || ""}
                  onChange={handleInputChange}
                />
              </label>
            </>
          )}
          <button type="submit">Add {selectedType}</button>
        </div>
      )}
    </form>
  );
};

export default DynamicForm;
