import React, { useState, useEffect } from "react";
import screenfull from "screenfull";
import axios from "axios";
import "./index.css"; // Ensure you import the CSS file

const OfficeScreen = ({
  officeScreenContent,
  handleDeleteItems,
  toggleLinkVisibility, // Receive toggle function
}) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);
  const [users, setUsers] = useState({}); // Store user data
  const [successMessage, setSuccessMessage] = useState(""); // State for success message
  const [confirmDelete, setConfirmDelete] = useState(false);

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
        toggleLinkVisibility(); // Call toggle function on fullscreen change
      };
      screenfull.on("change", changeHandler);
      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, [toggleLinkVisibility]);

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.key === "Escape" && screenfull.isFullscreen) {
        toggleLinkVisibility(); // Call toggle function on Escape key press in fullscreen mode
      }
    };

    document.addEventListener("keyup", handleKeyUp);

    return () => {
      document.removeEventListener("keyup", handleKeyUp);
    };
  }, [toggleLinkVisibility]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`);
      return response.data.name; // Adjust based on your user data structure
    } catch (error) {
      console.error(`Error fetching user data for user_id ${userId}:`, error);
      return "Unknown User";
    }
  };

  const fetchUsers = async () => {
    const uniqueUserIds = [
      ...new Set(
        officeScreenContent
          .filter((entry) => entry.table === "comments")
          .map((entry) => entry.user_id)
      ),
    ];
    const userData = {};
    for (const userId of uniqueUserIds) {
      userData[userId] = await fetchUserData(userId);
    }
    setUsers(userData);
  };

  useEffect(() => {
    fetchUsers();
  }, [officeScreenContent]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString("no-NO", options);
  };

  const renderEntry = (entry, index) => {
    let content;
    switch (entry.table) {
      case "events":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>
              {entry.title || "No Title"}
            </span>
            <span style={{ textAlign: "center" }}>
              {entry.description || "No Description"}
            </span>
            <span style={{ textAlign: "right" }}>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "meetings":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {entry.title || "No Title"}
            </span>
            <span style={{ textAlign: "center", flex: 1 }}>
              {entry.agenda || "No Agenda"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "canteenMenu":
        const menu = Array.isArray(entry.menu)
          ? entry.menu.join(", ")
          : entry.menu;
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {menu || "No Menu"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "comments":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {entry.content || "No Comment"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {users[entry.user_id] || "Unknown User"}
            </span>
          </div>
        );
        break;
      default:
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{entry.comment}</span>
          </div>
        );
    }

    return (
      <div
        key={index}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(entry)}
            onChange={() => toggleSelection(entry)}
          />
        )}
        {content}
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    setSelectedItems((prevSelected) => {
      if (prevSelected.includes(entry)) {
        return prevSelected.filter((item) => item !== entry);
      } else {
        return [...prevSelected, entry];
      }
    });
  };

  const handleDelete = () => {
    setConfirmDelete(true);
  };

  const confirmDeleteItems = () => {
    handleDeleteItems(selectedItems);
    setSuccessMessage("Successfully deleted!");
    setConfirmDelete(false);
    setEditMode(false); // Exit edit mode after deletion
  };

  const cancelDelete = () => {
    setConfirmDelete(false);
  };

  // Styles
  const containerStyle = {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "70px",
    padding: "20px",
  };

  const sectionStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)", // Adjusted background color with 30% opacity
    border: "3px solid rgba(227, 213, 202, 0.2)",
    borderRadius: "50px",
    padding: "20px",
    marginBottom: "0px",
    width: "500px", // Fixed width
    height: "auto", // Remove fixed height
    boxShadow: "0px 5px 10px rgba(0, 0, 0, 0.7)", // Drop shadow definition
    textAlign: "center",
    marginTop: "35px",
  };

  const titleStyle = {
    textAlign: "center", // Center the titles
    marginBottom: "30px",
    marginTop: "0px",
  };

  const subtitleStyle = {
    display: "flex",
    justifyContent: "space-between",
    padding: "0 20px",
    fontWeight: "bold",
  };

  return (
    <div className="animated-background">
      {showButton && (
        <div
          className="fullscreen-button-container"
          style={{ marginBottom: "20px", textAlign: "center" }}
        >
          <button onClick={toggleFullscreen}>
            {screenfull.isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          </button>
          {!screenfull.isFullscreen && (
            <button onClick={toggleEditMode}>
              {editMode ? "Cancel Edit" : "Edit"}
            </button>
          )}
        </div>
      )}

      <div style={containerStyle}>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Arrangementer</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "black" }}>
              Tittel
            </span>
            <span style={{ flex: 1, textAlign: "center", color: "black" }}>
              Beskrivelse
            </span>
            <span style={{ flex: 1, textAlign: "right", color: "black" }}>
              Dato
            </span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .slice(0, 3) // Limit to 3 items
              .map((event, index) => renderEntry(event, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>MÃ¸ter</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "black" }}>
              Tittel
            </span>
            <span style={{ flex: 1, textAlign: "center", color: "black" }}>
              Agenda
            </span>
            <span style={{ flex: 1, textAlign: "right", color: "black" }}>
              Dato
            </span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .slice(0, 3) // Limit to 3 items
              .map((meeting, index) => renderEntry(meeting, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Kantinemeny</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "black" }}>
              Dagens rett
            </span>
            <span style={{ flex: 1, textAlign: "right", color: "black" }}>
              Dato
            </span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .slice(0, 3) // Limit to 3 items
              .map((menu, index) => renderEntry(menu, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Beskjeder</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "blacK" }}></span>
            <span
              style={{ flex: 1, textAlign: "right", color: "Black" }}
            ></span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {officeScreenContent
              .filter((entry) => entry.table === "comments")
              .slice(0, 3) // Limit to 3 items
              .map((comment, index) => renderEntry(comment, index))}
          </ul>
        </div>
      </div>

      {confirmDelete && (
        <div
          style={{
            backgroundColor: "rgba(227, 213, 202, 0.2)",
            color: "black",
            border: "1px solid #f5c6cb",
            borderRadius: "4px",
            padding: "10px",
            marginTop: "20px",
            fontSize: "16px",
            fontWeight: "bold",
            display: "inline-flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <span style={{ marginRight: "10px", fontSize: "20px" }}>&#9888;</span>
          Are you sure you want to delete?
          <button onClick={confirmDeleteItems}>Yes</button>
          <button onClick={cancelDelete}>No</button>
        </div>
      )}

      {successMessage && (
        <div
          style={{
            backgroundColor: "rgba(227, 213, 202, 0.2)",
            color: "black",
            border: "1px solid #c3e6cb",
            borderRadius: "4px",
            padding: "10px",
            marginTop: "20px",
            fontSize: "16px",
            fontWeight: "bold",
            display: "inline-flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <span style={{ marginRight: "10px", fontSize: "20px" }}>
            &#10003;
          </span>
          {successMessage}
        </div>
      )}

      {editMode && (
        <button onClick={handleDelete} style={{ marginTop: "20px" }}>
          Remove Selected
        </button>
      )}
    </div>
  );
};

export default OfficeScreen;
