import React, { useState, useEffect } from "react";
import screenfull from "screenfull";

const OfficeScreen = ({ officeScreenContent }) => {
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "f" && (event.metaKey || event.ctrlKey)) {
        event.preventDefault();
        toggleFullscreen();
      }
    };

    document.addEventListener("keydown", handleKeyDown);

    // Set up the screenfull change event listener correctly
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        // Set edit mode based on fullscreen status directly
        setEditMode(false); // Always exit edit mode when the fullscreen status changes
        // React immediately to fullscreen status
        if (screenfull.isFullscreen) {
          // Actions to take specifically when entering fullscreen
        } else {
          // Actions to take specifically when exiting fullscreen
        }
      };

      screenfull.on("change", changeHandler);

      // Cleanup function
      return () => {
        document.removeEventListener("keydown", handleKeyDown);
        screenfull.off("change", changeHandler);
      };
    }

    // Cleanup function for the event listener if fullscreen is not enabled or when component unmounts
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry, index) => {
    return (
      <div key={index}>
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(index)}
            onChange={() => toggleSelection(index)}
          />
        )}
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]);
  };

  const toggleSelection = (index) => {
    setSelectedItems((prevSelected) => {
      const indexInArray = prevSelected.indexOf(index);
      if (indexInArray > -1) {
        return prevSelected.filter((item) => item !== index);
      } else {
        return [...prevSelected, index];
      }
    });
  };

  const removeSelectedItems = () => {
    console.log("Items to remove:", selectedItems);
    setSelectedItems([]);
    setEditMode(false);
  };

  return (
    <div>
      <div
        className={`signage-layout ${
          screenfull.isFullscreen ? "fullscreen" : ""
        }`}
      >
        {!screenfull.isFullscreen && (
          <>
            <button onClick={toggleEditMode}>
              {editMode ? "Cancel Edit" : "Edit"}
            </button>
            {editMode && (
              <button onClick={removeSelectedItems}>Remove Selected</button>
            )}
          </>
        )}

        {/* Content sections */}
        <div className="signage-events">
          <h3>Events</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map((event, index) => renderEntry(event, index))}
          </ul>
        </div>

        <div className="signage-meetings">
          <h3>Meetings</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map((meeting, index) => renderEntry(meeting, index))}
          </ul>
        </div>

        <div className="signage-canteen-menu">
          <h3>Canteen Menu</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map((menu, index) => renderEntry(menu, index))}
          </ul>
        </div>

        <div className="signage-other-content">
          <h3>Other Content</h3>
          <ul>
            {officeScreenContent
              .filter(
                (entry) =>
                  entry.table !== "events" &&
                  entry.table !== "meetings" &&
                  entry.table !== "canteenMenu"
              )
              .map((other, index) => renderEntry(other, index))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default OfficeScreen;
