import React, { useState, useEffect } from "react";
import screenfull from "screenfull";

const OfficeScreen = ({ officeScreenContent, handleDeleteItems }) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
      };
      screenfull.on("change", changeHandler);
      return () => screenfull.off("change", changeHandler);
    }
  }, []);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry, index) => (
    <div key={index}>
      {editMode && (
        <input
          type="checkbox"
          checked={selectedItems.includes(entry)}
          onChange={() => toggleSelection(entry)}
        />
      )}
      {Object.entries(entry).map(([key, value]) => (
        <p key={key}>
          <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
          {key === "date" || key === "birthdate" ? formatDate(value) : value}
        </p>
      ))}
    </div>
  );

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    const index = selectedItems.indexOf(entry);
    if (index > -1) {
      setSelectedItems((prev) => prev.filter((item) => item !== entry));
    } else {
      setSelectedItems((prev) => [...prev, entry]);
    }
  };

  const handleDelete = () => {
    handleDeleteItems(selectedItems);
    setSelectedItems([]);
  };

  // Styles
  const containerStyle = {
    display: "grid",
    gridTemplateColumns: "1fr 1fr", // Two columns
    gap: "20px",
    padding: "20px",
  };

  const sectionStyle = {
    backgroundColor: "#f8f9fa",
    border: "1px solid #dee2e6",
    borderRadius: "5px",
    padding: "20px",
    marginBottom: "20px",
  };

  return (
    <div>
      {showButton && (
        <div
          className="fullscreen-button-container"
          style={{ marginBottom: "20px" }}
        >
          <button onClick={toggleFullscreen}>
            {screenfull.isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          </button>
          {!screenfull.isFullscreen && (
            <button onClick={toggleEditMode}>
              {editMode ? "Cancel Edit" : "Edit"}
            </button>
          )}
        </div>
      )}

      <div style={containerStyle}>
        <div style={sectionStyle}>
          <h3>Events</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map((event, index) => renderEntry(event, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3>Meetings</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map((meeting, index) => renderEntry(meeting, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3>Canteen Menu</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map((menu, index) => renderEntry(menu, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3>Other Content</h3>
          <ul>
            {officeScreenContent
              .filter(
                (entry) =>
                  entry.table !== "events" &&
                  entry.table !== "meetings" &&
                  entry.table !== "canteenMenu"
              )
              .map((other, index) => renderEntry(other, index))}
          </ul>
        </div>
      </div>

      {editMode && (
        <button onClick={handleDelete} style={{ marginTop: "20px" }}>
          Remove Selected
        </button>
      )}
    </div>
  );
};

export default OfficeScreen;
