import React, { useState, useEffect } from "react";
import screenfull from "screenfull";
import axios from "axios";

const OfficeScreen = ({
  officeScreenContent,
  handleDeleteItems,
  toggleLinkVisibility, // Receive toggle function
}) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);
  const [users, setUsers] = useState({}); // Store user data

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
        toggleLinkVisibility(); // Call toggle function on fullscreen change
      };
      screenfull.on("change", changeHandler);
      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, [toggleLinkVisibility]);

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.key === "Escape" && screenfull.isFullscreen) {
        toggleLinkVisibility(); // Call toggle function on Escape key press in fullscreen mode
      }
    };

    document.addEventListener("keyup", handleKeyUp);

    return () => {
      document.removeEventListener("keyup", handleKeyUp);
    };
  }, [toggleLinkVisibility]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`);
      return response.data.name; // Adjust based on your user data structure
    } catch (error) {
      console.error(`Error fetching user data for user_id ${userId}:`, error);
      return "Unknown User";
    }
  };

  const fetchUsers = async () => {
    const uniqueUserIds = [
      ...new Set(
        officeScreenContent
          .filter((entry) => entry.table === "comments")
          .map((entry) => entry.user_id)
      ),
    ];
    const userData = {};
    for (const userId of uniqueUserIds) {
      userData[userId] = await fetchUserData(userId);
    }
    setUsers(userData);
  };

  useEffect(() => {
    fetchUsers();
  }, [officeScreenContent]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString("no-NO", options);
  };

  const renderEntry = (entry, index) => {
    let content;
    switch (entry.table) {
      case "events":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>
              {entry.title || "No Title"}
            </span>
            <span style={{ textAlign: "center" }}>
              {entry.description || "No Description"}
            </span>
            <span style={{ textAlign: "right" }}>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "meetings":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {entry.title || "No Title"}
            </span>
            <span style={{ textAlign: "center", flex: 1 }}>
              {entry.agenda || "No Agenda"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "canteenMenu":
        const menu = Array.isArray(entry.menu)
          ? entry.menu.join(", ")
          : entry.menu;
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {menu || "No Menu"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "comments":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left", flex: 1 }}>
              {entry.content || "No Comment"}
            </span>
            <span style={{ textAlign: "right", flex: 1 }}>
              {users[entry.user_id] || "Unknown User"}
            </span>
          </div>
        );
        break;
      default:
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span style={{ textAlign: "left" }}>{entry.comment}</span>
          </div>
        );
    }

    return (
      <div
        key={index}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(entry)}
            onChange={() => toggleSelection(entry)}
          />
        )}
        {content}
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    const index = selectedItems.indexOf(entry);
    if (index > -1) {
