import React, { useState, useEffect } from "react";
import screenfull from "screenfull";

const OfficeScreen = ({
  officeScreenContent,
  handleDeleteItems,
  toggleLinkVisibility, // Receive toggle function
}) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
        toggleLinkVisibility(); // Call toggle function on fullscreen change
      };
      screenfull.on("change", changeHandler);
      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, [toggleLinkVisibility]);

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.key === "Escape" && screenfull.isFullscreen) {
        toggleLinkVisibility(); // Call toggle function on Escape key press in fullscreen mode
      }
    };

    document.addEventListener("keyup", handleKeyUp);

    return () => {
      document.removeEventListener("keyup", handleKeyUp);
    };
  }, [toggleLinkVisibility]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString("no-NO", options);
  };

  const renderEntry = (entry, index) => {
    let content;
    switch (entry.table) {
      case "events":
      case "meetings":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <span>{entry.title}</span>
            <span>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "canteenMenu":
        const menu = Array.isArray(entry.menu)
          ? entry.menu.join(", ")
          : entry.menu;
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <span>{menu}</span>
            <span>{formatDate(entry.date)}</span>
          </div>
        );
        break;
      case "comments":
        content = <p key={index}>{entry.content}</p>; // Updated to use entry.content
        break;
      default:
        content = <p key={index}>{entry.comment}</p>;
    }

    return (
      <div
        key={index}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(entry)}
            onChange={() => toggleSelection(entry)}
          />
        )}
        {content}
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    const index = selectedItems.indexOf(entry);
    if (index > -1) {
      setSelectedItems((prev) => prev.filter((item) => item !== entry));
    } else {
      setSelectedItems((prev) => [...prev, entry]);
    }
  };

  const handleDelete = () => {
    handleDeleteItems(selectedItems);
    setSelectedItems([]);
    toggleEditMode(); // Reset edit mode after deleting selected items
  };

  // Styles
  const containerStyle = {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "70px",
    padding: "20px",
  };

  const sectionStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)", // Adjusted background color with 30% opacity
    border: "1px solid #dee2e6",
    borderRadius: "5px",
    padding: "20px",
    marginBottom: "20px",
    width: "500px", // Fixed width
    height: "250px", // Fixed height
    overflow: "auto", // Makes content scrollable within the box
    boxShadow: "0px 5px 10px rgba(0, 0, 0, 0.1)", // Drop shadow definition
  };

  const entryStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
  };

  const entryTextStyle = {
    flex: 1,
    textAlign: "left",
  };

  const entryDateStyle = {
    textAlign: "right",
    whiteSpace: "nowrap",
  };

  return (
    <div>
      {showButton && (
        <div
          className="fullscreen-button-container"
          style={{ marginBottom: "20px", textAlign: "center" }}
        >
          <button onClick={toggleFullscreen}>
            {screenfull.isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          </button>
          {!screenfull.isFullscreen && (
            <button onClick={toggleEditMode}>
              {editMode ? "Cancel Edit" : "Edit"}
            </button>
          )}
        </div>
      )}

      <div style={containerStyle}>
        <div style={sectionStyle}>
          <h3 style={{ textAlign: "left" }}>Events</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map((event, index) => (
                <div key={index} style={entryStyle}>
                  <span style={entryTextStyle}>{event.title}</span>
                  <span style={entryDateStyle}>{formatDate(event.date)}</span>
                </div>
              ))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={{ textAlign: "left" }}>Meetings</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map((meeting, index) => (
                <div key={index} style={entryStyle}>
                  <span style={entryTextStyle}>{meeting.title}</span>
                  <span style={entryDateStyle}>{formatDate(meeting.date)}</span>
                </div>
              ))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={{ textAlign: "left" }}>Canteen Menu</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map((menu, index) => (
                <div key={index} style={entryStyle}>
                  <span style={entryTextStyle}>{menu.menu.join(", ")}</span>
                  <span style={entryDateStyle}>{formatDate(menu.date)}</span>
                </div>
              ))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={{ textAlign: "left" }}>Comments</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "comments")
              .map((comment, index) => (
                <div key={index} style={entryStyle}>
                  <span style={entryTextStyle}>{comment.content}</span>
                  <span style={entryDateStyle}>
                    {comment.date && formatDate(comment.date)}
                  </span>
                </div>
              ))}
          </ul>
        </div>
      </div>

      {editMode && (
        <button onClick={handleDelete} style={{ marginTop: "20px" }}>
          Remove Selected
        </button>
      )}
    </div>
  );
};

export default OfficeScreen;
