import React, { useState, useEffect } from "react";
import screenfull from "screenfull";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashAlt, faEdit, faTimes } from "@fortawesome/free-solid-svg-icons";
import Entur from "./Entur"; // Import Entur component

const OfficeScreen = ({
  officeScreenContent,
  handleDeleteItems,
  toggleLinkVisibility,
}) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);
  const [users, setUsers] = useState({});
  const [departures, setDepartures] = useState([]); // State for departures

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
        toggleLinkVisibility();
      };
      screenfull.on("change", changeHandler);
      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, [toggleLinkVisibility]);

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.key === "Escape" && screenfull.isFullscreen) {
        toggleLinkVisibility();
      }
    };

    document.addEventListener("keyup", handleKeyUp);

    return () => {
      document.removeEventListener("keyup", handleKeyUp);
    };
  }, [toggleLinkVisibility]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`);
      return response.data.name;
    } catch (error) {
      console.error(`Error fetching user data for user_id ${userId}:`, error);
      return "Unknown User";
    }
  };

  const fetchUsers = async () => {
    const uniqueUserIds = [
      ...new Set(
        officeScreenContent
          .filter((entry) => entry.table === "comments")
          .map((entry) => entry.user_id)
      ),
    ];
    const userData = {};
    for (const userId of uniqueUserIds) {
      userData[userId] = await fetchUserData(userId);
    }
    setUsers(userData);
  };

  useEffect(() => {
    fetchUsers();
  }, [officeScreenContent]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString("no-NO", options);
  };

  const renderEntry = (entry, index) => {
    let content;
    switch (entry.table) {
      case "events":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span
              style={{ textAlign: "left", color: "rgba(227, 213, 202, 0.2)" }}
            >
              {entry.title || "No Title"}
            </span>
            <span
              style={{ textAlign: "center", color: "rgba(227, 213, 202, 0.2)" }}
            >
              {entry.description || "No Description"}
            </span>
            <span
              style={{ textAlign: "right", color: "rgba(227, 213, 202, 0.2)" }}
            >
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "canteenMenu":
        const menu = Array.isArray(entry.menu)
          ? entry.menu.join(", ")
          : entry.menu;
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span
              style={{
                textAlign: "left",
                flex: 1,
                color: "rgba(227, 213, 202, 0.2)",
              }}
            >
              {menu || "No Menu"}
            </span>
            <span
              style={{
                textAlign: "right",
                flex: 1,
                color: "rgba(227, 213, 202, 0.2)",
              }}
            >
              {formatDate(entry.date)}
            </span>
          </div>
        );
        break;
      case "comments":
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span
              style={{
                textAlign: "left",
                flex: 1,
                color: "rgba(227, 213, 202, 0.2)",
              }}
            >
              {entry.content || "No Comment"}
            </span>
            <span
              style={{
                textAlign: "right",
                flex: 1,
                color: "rgba(227, 213, 202, 0.2)",
              }}
            >
              {users[entry.user_id] || "Unknown User"}
            </span>
          </div>
        );
        break;
      default:
        content = (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              width: "100%",
              padding: "20px",
            }}
          >
            <span
              style={{ textAlign: "left", color: "rgba(227, 213, 202, 0.2)" }}
            >
              {entry.comment}
            </span>
          </div>
        );
    }

    return (
      <div
        key={index}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        {editMode && (
          <input
            type="checkbox"
            checked={selectedItems.includes(entry)}
            onChange={() => toggleSelection(entry)}
          />
        )}
        {content}
      </div>
    );
  };

  const renderDeparture = (departure, index) => {
    return (
      <div
        key={index}
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          width: "100%",
          padding: "20px",
        }}
      >
        <span
          style={{
            textAlign: "left",
            flex: 1,
            color: "rgba(227, 213, 202, 0.2)",
          }}
        >
          {departure.serviceJourney.journeyPattern.line.name || "No Title"}
        </span>
        <span
          style={{
            textAlign: "center",
            flex: 1,
            color: "rgba(227, 213, 202, 0.2)",
          }}
        >
          {departure.destinationDisplay.frontText || "No Destination"}
        </span>
        <span
          style={{
            textAlign: "right",
            flex: 1,
            color: "rgba(227, 213, 202, 0.2)",
          }}
        >
          {new Date(departure.expectedArrivalTime).toLocaleTimeString()}
        </span>
      </div>
    );
  };

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]); // Clear selections when toggling edit mode
  };

  const toggleSelection = (entry) => {
    setSelectedItems((prevSelected) => {
      if (prevSelected.includes(entry)) {
        return prevSelected.filter((item) => item !== entry);
      } else {
        return [...prevSelected, entry];
      }
    });
  };

  const handleDelete = () => {
    handleDeleteItems(selectedItems);
    setEditMode(false); // Toggle back to normal mode
    setSelectedItems([]); // Clear selected items
  };

  // Sort entries by date in ascending order
  const sortedEntries = (entries) => {
    return entries.sort((a, b) => new Date(a.date) - new Date(b.date));
  };

  // Styles
  const containerStyle = {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "70px",
    padding: "20px",
  };

  const sectionStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)",
    color: "black",
    borderRadius: "25px",
    padding: "20px",
    marginBottom: "0px",
    width: "400px",
    height: "auto",
    boxShadow: "0px 5px 10px rgba(0, 0, 0, 0.2)",
    textAlign: "center",
    marginTop: "35px",
  };

  const titleStyle = {
    textAlign: "center",
    marginBottom: "30px",
    marginTop: "0px",
    color: "white",
  };

  const subtitleStyle = {
    display: "flex",
    justifyContent: "space-between",
    padding: "0 20px",
    fontWeight: "bold",
  };

  const iconButtonStyle = {
    backgroundColor: "transparent",
    border: "none",
    cursor: "pointer",
    fontSize: "20px",
    marginLeft: "10px",
    marginTop: "20px",
  };

  return (
    <div
      style={{
        backgroundColor: "#f4f4f8;",
        minHeight: "100vh",
        padding: "20px",
      }}
    >
      {showButton && (
        <div
          className="fullscreen-button-container"
          style={{
            marginTop: "20px",
            marginBottom: "20px",
            textAlign: "right",
            fontSize: "20px",
            marginRight: "20px",
          }}
        >
          <button onClick={toggleFullscreen}>
            {screenfull.isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          </button>
          {!screenfull.isFullscreen && (
            <>
              <button onClick={toggleEditMode}>
                {editMode ? (
                  <>
                    <FontAwesomeIcon icon={faTimes} />
                    Cancel Edit
                  </>
                ) : (
                  <>
                    <FontAwesomeIcon icon={faEdit} />
                    Edit
                  </>
                )}
              </button>
              {editMode && (
                <button
                  onClick={handleDelete}
                  style={iconButtonStyle}
                  title="Delete Selected"
                >
                  <FontAwesomeIcon icon={faTrashAlt} />
                </button>
              )}
            </>
          )}
        </div>
      )}

      <div style={containerStyle}>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Arrangementer</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "white" }}>
              Tittel
            </span>
            <span style={{ flex: 1, textAlign: "center", color: "white" }}>
              Beskrivelse
            </span>
            <span style={{ flex: 1, textAlign: "right", color: "white" }}>
              Dato
            </span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {sortedEntries(
              officeScreenContent.filter((entry) => entry.table === "events")
            )
              .slice(0, 3) // Limit to 3 items
              .map((event, index) => renderEntry(event, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={{ ...titleStyle, marginBottom: "10px" }}>
            Majorstuen T-banestasjon
          </h3>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              fontWeight: "bold",
              color: "white",
            }}
          >
            <span style={{ textAlign: "left" }}>Linje</span>
            <span style={{ textAlign: "right" }}>Avgang</span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            <Entur setDepartures={setDepartures} />
            {departures.map((departure, index) =>
              renderDeparture(departure, index)
            )}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={{ ...titleStyle, marginBottom: "10px" }}>Kantinemeny</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "white" }}>
              Dagens rett
            </span>
            <span style={{ flex: 1, textAlign: "right", color: "white" }}>
              Dato
            </span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {sortedEntries(
              officeScreenContent.filter(
                (entry) => entry.table === "canteenMenu"
              )
            )
              .slice(0, 3) // Limit to 3 items
              .map((menu, index) => renderEntry(menu, index))}
          </ul>
        </div>
        <div style={sectionStyle}>
          <h3 style={titleStyle}>Beskjeder</h3>
          <div style={subtitleStyle}>
            <span style={{ flex: 1, textAlign: "left", color: "white" }}></span>
            <span
              style={{ flex: 1, textAlign: "right", color: "white" }}
            ></span>
          </div>
          <ul style={{ listStyleType: "none", padding: 0 }}>
            {sortedEntries(
              officeScreenContent.filter((entry) => entry.table === "comments")
            )
              .slice(0, 3) // Limit to 3 items
              .map((comment, index) => renderEntry(comment, index))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default OfficeScreen;
