import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";

function App() {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);

  // Consolidate side effects for localStorage management
  useEffect(() => {
    // Function to load content from localStorage
    const loadContent = () => {
      const savedContent = localStorage.getItem("officeScreenContent");
      const parsedContent = savedContent ? JSON.parse(savedContent) : [];
      setOfficeScreenContent(parsedContent);
      console.log("LocalStorage data loaded:", parsedContent);
    };

    // Set up event listener for beforeunload event
    const saveContentBeforeUnload = () => {
      const contentToSave = JSON.stringify(officeScreenContent);
      localStorage.setItem("officeScreenContent", contentToSave);
      console.log("LocalStorage data saved before refresh:", contentToSave);
    };

    loadContent();
    window.addEventListener("beforeunload", saveContentBeforeUnload);

    // Cleanup the event listener on component unmount
    return () =>
      window.removeEventListener("beforeunload", saveContentBeforeUnload);
  }, [officeScreenContent]);

  // Function to update officeScreenContent
  const updateOfficeScreenContent = (newContent) => {
    setOfficeScreenContent((prevContent) => {
      const updatedContent = [...prevContent, ...newContent];
      if (JSON.stringify(updatedContent) !== JSON.stringify(prevContent)) {
        localStorage.setItem(
          "officeScreenContent",
          JSON.stringify(updatedContent)
        );
      }
      return updatedContent;
    });
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/add">Add Data</Link>
            </li>
            <li>
              <Link to="/view">View Content</Link>
            </li>
            <li>
              <Link to="/office-screen">Office Screen</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route
            path="/"
            element={<h1>Welcome to the Digital Signage System</h1>}
          />
          <Route path="/add" element={<DynamicForm />} />
          <Route
            path="/view"
            element={
              <ViewContent setOfficeScreenContent={updateOfficeScreenContent} />
            }
          />
          <Route
            path="/office-screen"
            element={<OfficeScreen officeScreenContent={officeScreenContent} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
