import React, { useState, useEffect } from "react";

const OfficeScreen = ({ officeScreenContent }) => {
  const [content, setContent] = useState([]);

  useEffect(() => {
    // Check if there's data in localStorage
    const savedContent = localStorage.getItem("officeScreenContent");

    if (savedContent) {
      // If data exists, load it into state
      setContent(JSON.parse(savedContent));
    } else {
      // If not, use the initial content
      setContent(officeScreenContent);
    }
  }, [officeScreenContent]);

  // Save content to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("officeScreenContent", JSON.stringify(content));
  }, [content]);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry) => {
    return (
      <div>
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>Office Screen</h1>
      <div className="signage-layout">
        <div className="signage-events">
          <h3>Events</h3>
          <ul>
            {content
              .filter((entry) => entry.table === "events")
              .map((event, index) => (
                <li key={index}>{renderEntry(event)}</li>
              ))}
          </ul>
        </div>
        {/* Similar code for other sections */}
      </div>
    </div>
  );
};

export default OfficeScreen;
