import axios from "axios";

const API_URL = "http://localhost:3000/comments"; // Adjust if your server runs on a different URL or port

export const getComments = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error("Error fetching comments:", error);
    return []; // Return an empty array on error
  }
};

export const getCommentsByMeetingId = async (meetingId) => {
  try {
    const response = await axios.get(`${API_URL}/meeting/${meetingId}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching comments for meeting ${meetingId}:`, error);
    return []; // Return an empty array on error
  }
};

export const createComment = async (comment) => {
  try {
    const response = await axios.post(API_URL, comment);
    return response.data;
  } catch (error) {
    console.error("Error creating comment:", error);
    throw error;
  }
};

export const updateComment = async (id, comment) => {
  try {
    const response = await axios.put(`${API_URL}/${id}`, comment);
    return response.data;
  } catch (error) {
    console.error(`Error updating comment ${id}:`, error);
    throw error;
  }
};

export const deleteComment = async (id) => {
  try {
    const response = await axios.delete(`${API_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error deleting comment ${id}:`, error);
    throw error;
  }
};
