import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [users, setUsers] = useState([]);
  const [comments, setComments] = useState([]);
  const [canteenMenu, setCanteenMenu] = useState([]);
  const [events, setEvents] = useState([]);
  const [meetings, setMeetings] = useState([]);
  const [error, setError] = useState(null);

  console.log("App component rendered"); // This should log every time the component renders

  useEffect(() => {
    console.log("useEffect executed"); // This should log when the useEffect is executed

    const fetchData = async () => {
      try {
        console.log("Fetching users...");
        const userResponse = await axios.get("http://localhost:3000/Users");
        console.log("User response data:", userResponse.data);
        setUsers(userResponse.data);

        console.log("Fetching comments...");
        const commentResponse = await axios.get(
          "http://localhost:3000/comments"
        );
        console.log("Comment response data:", commentResponse.data);
        setComments(commentResponse.data);

        console.log("Fetching canteen menu...");
        const canteenMenuResponse = await axios.get(
          "http://localhost:3000/canteenMenu"
        );
        console.log("Canteen menu response data:", canteenMenuResponse.data);
        setCanteenMenu(canteenMenuResponse.data);

        console.log("Fetching events...");
        const eventResponse = await axios.get("http://localhost:3000/event");
        console.log("Event response data:", eventResponse.data);
        setEvents(eventResponse.data);

        console.log("Fetching meetings...");
        const meetingResponse = await axios.get(
          "http://localhost:3000/meetings"
        );
        console.log("Meeting response data:", meetingResponse.data);
        setMeetings(meetingResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        setError(error);
      }
    };

    fetchData(); // Call the async function to fetch data
  }, []);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div>
      <h1>Data from API</h1>
      {error && <p>Error fetching data: {error.message}</p>}
      <h2>Users</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>{user.name}</li>
        ))}
      </ul>
      <h2>Comments</h2>
      <ul>
        {comments.map((comment) => (
          <li key={comment.id}>
            <strong>Content:</strong> {comment.content}
          </li>
        ))}
      </ul>
      <h2>Canteen Menu</h2>
      <ul>
        {canteenMenu.map((item) => (
          <li key={item.id}>
            <strong>Date:</strong> {item.date} <br />
            <strong>Menu:</strong> {item.menu}
          </li>
        ))}
      </ul>
      <h2>Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event.id}>{event.title}</li>
        ))}
      </ul>
      <h2>Meetings</h2>
      <ul>
        {meetings.map((meeting) => (
          <li key={meeting.id}>{meeting.title}</li>
        ))}
      </ul>
    </div>
  );
};
export default App;
