import React, { useEffect, useState } from "react";
import axios from "axios";

const OfficeScreen = () => {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);

  useEffect(() => {
    const fetchOfficeScreenContent = async () => {
      try {
        const response = await axios.get("http://localhost:3000/officeScreen");
        setOfficeScreenContent(response.data);
      } catch (error) {
        console.error("Error fetching office screen content:", error);
      }
    };

    fetchOfficeScreenContent();
  }, []);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry) => {
    return (
      <div>
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>Office Screen</h1>
      <div className="signage-layout">
        <div className="signage-events">
          <h3>Events</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map((event, index) => (
                <li key={index}>{renderEntry(event)}</li>
              ))}
          </ul>
        </div>
        <div className="signage-meetings">
          <h3>Meetings</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map((meeting, index) => (
                <li key={index}>{renderEntry(meeting)}</li>
              ))}
          </ul>
        </div>
        <div className="signage-canteen-menu">
          <h3>Canteen Menu</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map((menu, index) => (
                <li key={index}>{renderEntry(menu)}</li>
              ))}
          </ul>
        </div>
        <div className="signage-other-content">
          <h3>Other Content</h3>
          <ul>
            {officeScreenContent
              .filter(
                (entry) =>
                  entry.table !== "events" &&
                  entry.table !== "meetings" &&
                  entry.table !== "canteenMenu"
              )
              .map((other, index) => (
                <li key={index}>{renderEntry(other)}</li>
              ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default OfficeScreen;
