// Home.js
import React, { useRef } from "react";
import homeImage from "./images/home-image.png"; // Import the image
import howToImage from "./images/Howto.png";

const Home = () => {
  const nextSectionRef = useRef(null);

  const handleScrollDown = () => {
    if (nextSectionRef.current) {
      nextSectionRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div>
      <div
        style={{
          display: "flex",
          flexDirection: "column", // Align items vertically
          justifyContent: "center", // Center vertically
          alignItems: "center", // Center horizontally
          height: "80vh",
          paddingLeft: "20px",
          paddingRight: "20px",
          marginTop: "0px", // Add top margin here
        }}
      >
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center", // Center text horizontally
            textAlign: "center",
            marginRight: "20px",
          }}
        >
          <h1
            style={{
              fontSize: "40px",
              fontWeight: "bold",
              color: "black",
              marginBottom: "20px",
              marginTop: "0",
            }}
          >
            Enkel og effektiv informasjonvisning
          </h1>

          <h2
            style={{
              fontSize: "20px",
              fontWeight: "normal",
              color: "white",
              marginTop: "0",
            }}
          >
            Sanntidsinformasjon på jobben. Tilpass innholdet med noen få enkle
            trinn.
          </h2>
        </div>

        <img
          src={homeImage}
          alt="Home"
          style={{
            width: "500px",
            height: "auto",
            boxShadow: "0px 5px 10px rgba(0, 0, 0, 0.5)", // Drop shadow definition
            marginTop: "20px", // Add some margin to the top of the image
          }}
        />

        <div
          onClick={handleScrollDown}
          style={{
            cursor: "pointer",
            marginTop: "20px",
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="50"
            height="50"
            fill="currentColor"
            className="bi bi-arrow-down-circle"
            viewBox="0 0 16 16"
            style={{ color: "gray" }}
          >
            <path
              fillRule="evenodd"
              d="M8 0a8 8 0 1 1 0 16A8 8 0 0 1 8 0zm0 1a7 7 0 1 0 0 14A7 7 0 0 0 8 1zm3.5 6.5a.5.5 0 0 1 0 1H9v3.5a.5.5 0 0 1-1 0V8H4.5a.5.5 0 0 1 0-1h3v-3a.5.5 0 0 1 1 0v3h2.5z"
            />
          </svg>
        </div>
      </div>

      <div
        ref={nextSectionRef}
        style={{
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: "#f0f0f0", // Light background color for contrast
          padding: "20px",
        }}
      >
        <h2
          style={{
            fontSize: "30px",
            fontWeight: "bold",
            color: "black",
            textAlign: "center",
            marginBottom: "20px", // Margin to separate the texts
          }}
        >
          Slik kommer du i gang
        </h2>
        <p
          style={{
            fontSize: "20px",
            fontWeight: "normal",
            color: "black",
            textAlign: "center",
            maxWidth: "600px", // Limit the width for better readability
          }}
        >
          Naviger til "Add Data" og velg hva slags type innhold du vil legge til
          i databasen.
        </p>

        <img
          src={howToImage}
          alt="Add Data"
          style={{
            width: "500px",
            height: "auto",
            marginTop: "20px", // Add some margin to the top of the image
          }}
        />
      </div>
    </div>
  );
};

export default Home;
