import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";

function App() {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);

  // Combined useEffect for loading from and saving to localStorage
  useEffect(() => {
    // Load content from localStorage on mount
    const loadFromLocalStorage = () => {
      const savedContent = localStorage.getItem("officeScreenContent");
      if (savedContent) {
        const parsedContent = JSON.parse(savedContent);
        setOfficeScreenContent(parsedContent);
        console.log("Data loaded from localStorage:", parsedContent);
      } else {
        console.log("No data found in localStorage.");
      }
    };

    loadFromLocalStorage();

    // Setup event listener for before page unload
    const handleBeforeUnload = () => {
      const currentContent = localStorage.getItem("officeScreenContent");
      console.log("Final localStorage data before refresh:", currentContent);
    };

    window.addEventListener("beforeunload", handleBeforeUnload);

    // Cleanup the event listener on unmount
    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []); // Ensures this effect runs only once on mount

  // Function to update officeScreenContent and save to localStorage
  const updateOfficeScreenContent = (newContent) => {
    setOfficeScreenContent((prevContent) => {
      const updatedContent = [...prevContent, ...newContent];
      if (JSON.stringify(updatedContent) !== JSON.stringify(prevContent)) {
        localStorage.setItem(
          "officeScreenContent",
          JSON.stringify(updatedContent)
        );
      }
      return updatedContent;
    });
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/add">Add Data</Link>
            </li>
            <li>
              <Link to="/view">View Content</Link>
            </li>
            <li>
              <Link to="/office-screen">Office Screen</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route
            path="/"
            element={<h1>Welcome to the Digital Signage System</h1>}
          />
          <Route path="/add" element={<DynamicForm />} />
          <Route
            path="/view"
            element={
              <ViewContent setOfficeScreenContent={updateOfficeScreenContent} />
            }
          />
          <Route
            path="/office-screen"
            element={<OfficeScreen officeScreenContent={officeScreenContent} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
