import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";
import DeleteFromDatabase from "./DeleteFromDatabase";
import screenfull from "screenfull";
import homeImage from "./images/home-image.png"; // Import the image

const App = () => {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);
  const [showLinks, setShowLinks] = useState(true); // State to control link visibility

  useEffect(() => {
    const savedContent = localStorage.getItem("officeScreenContent");
    if (savedContent) {
      setOfficeScreenContent(JSON.parse(savedContent));
    }
  }, []);

  useEffect(() => {
    if (officeScreenContent.length !== 0) {
      localStorage.setItem(
        "officeScreenContent",
        JSON.stringify(officeScreenContent)
      );
    }
  }, [officeScreenContent]);

  const handleDeleteItems = (entries) => {
    const updatedContent = officeScreenContent.filter(
      (item) =>
        !entries.some(
          (entry) => item.id === entry.id && item.table === entry.table
        )
    );
    setOfficeScreenContent(updatedContent);
    localStorage.setItem("officeScreenContent", JSON.stringify(updatedContent));
  };

  const toggleLinkVisibility = () => {
    setShowLinks(!showLinks);
  };

  return (
    <Router>
      <div>
        <nav
          style={{
            backgroundColor: "rgba(227, 213, 202, 0.2)",
            padding: "40px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            margin: 0,
            boxSizing: "border-box",
          }}
        >
          <div style={{ fontSize: "40px", fontWeight: "bold" }}>Lightbeam</div>
          <ul
            style={{
              listStyleType: "none",
              display: "flex",
              gap: "10px",
              visibility: showLinks ? "visible" : "hidden",
              margin: 0,
              padding: 0,
            }}
          >
            <li>
              <Link
                to="/"
                style={{ textDecoration: "none", color: "gray" }}
                onMouseOver={(e) => (e.target.style.color = "black")}
                onMouseOut={(e) => (e.target.style.color = "gray")}
              >
                Home
              </Link>
            </li>
            <li>
              <Link
                to="/add"
                style={{ textDecoration: "none", color: "gray" }}
                onMouseOver={(e) => (e.target.style.color = "black")}
                onMouseOut={(e) => (e.target.style.color = "gray")}
              >
                Add Data
              </Link>
            </li>
            <li>
              <Link
                to="/view"
                style={{ textDecoration: "none", color: "gray" }}
                onMouseOver={(e) => (e.target.style.color = "black")}
                onMouseOut={(e) => (e.target.style.color = "gray")}
              >
                View Content
              </Link>
            </li>
            <li>
              <Link
                to="/office-screen"
                style={{ textDecoration: "none", color: "gray" }}
                onMouseOver={(e) => (e.target.style.color = "black")}
                onMouseOut={(e) => (e.target.style.color = "gray")}
              >
                Office Screen
              </Link>
            </li>
            <li>
              <Link
                to="/delete"
                style={{ textDecoration: "none", color: "gray" }}
                onMouseOver={(e) => (e.target.style.color = "black")}
                onMouseOut={(e) => (e.target.style.color = "gray")}
              >
                Delete Data
              </Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "flex-start",
                  height: "80vh",
                  paddingLeft: "20px",
                  paddingRight: "20px",
                  marginTop: "200px", // Add top margin here
                }}
              >
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "flex-start",
                    marginRight: "20px",
                  }}
                >
                  <h1
                    style={{
                      fontSize: "40px",
                      fontWeight: "bold",
                      color: "black",
                      marginBottom: "20px",
                      marginTop: "0",
                    }}
                  >
                    Enkel og effektiv informasjonvisning
                  </h1>

                  <h2
                    style={{
                      fontSize: "20px",
                      fontWeight: "normal",
                      color: "gray",
                      marginTop: "0",
                    }}
                  >
                    Sanntidsinformasjon på jobben. Tilpass innholdet med noen få
                    enkle trinn.
                  </h2>
                </div>

                <img
                  src={homeImage}
                  alt="Home"
                  style={{
                    width: "500px",
                    height: "auto",
                  }}
                />
              </div>
            }
          />
          <Route path="/add" element={<DynamicForm />} />
          <Route
            path="/view"
            element={
              <ViewContent setOfficeScreenContent={setOfficeScreenContent} />
            }
          />
          <Route
            path="/office-screen"
            element={
              <OfficeScreen
                officeScreenContent={officeScreenContent}
                handleDeleteItems={handleDeleteItems}
                toggleLinkVisibility={toggleLinkVisibility} // Pass the toggle function
              />
            }
          />
          <Route path="/delete" element={<DeleteFromDatabase />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
