import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ViewContent = ({ setOfficeScreenContent }) => {
  const [selectedTable, setSelectedTable] = useState("");
  const [tableData, setTableData] = useState([]);
  const [selectedEntries, setSelectedEntries] = useState([]);
  const navigate = useNavigate();

  const fetchTableData = async (table) => {
    try {
      const response = await axios.get(`http://localhost:3000/${table}`);
      setTableData(response.data);
    } catch (error) {
      console.error(`Error fetching data from ${table}:`, error);
    }
  };

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
    if (e.target.value) {
      fetchTableData(e.target.value);
    } else {
      setTableData([]);
    }
  };

  const handleCheckboxChange = (entry) => {
    setSelectedEntries((prevEntries) => {
      if (prevEntries.includes(entry)) {
        return prevEntries.filter((e) => e !== entry);
      } else {
        return [...prevEntries, entry];
      }
    });
  };

  const formatDate = (dateString) => {
    const options = {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const handleAddToScreen = async () => {
    const promises = selectedEntries.map((entry) => {
      return axios.get(`http://localhost:3000/${selectedTable}/${entry.id}`);
    });

    try {
      const results = await Promise.all(promises);
      const entriesData = results.map((result) => ({
        ...result.data,
        table: selectedTable,
      }));

      // Log the entries being added
      console.log("Entries Data:", entriesData);

      // Merge new data with existing data from localStorage
      setOfficeScreenContent((prevContent) => {
        const savedContent = localStorage.getItem("officeScreenContent");
        const parsedContent = savedContent ? JSON.parse(savedContent) : [];
        const newContent = [...parsedContent, ...entriesData];
        console.log("New OfficeScreenContent:", newContent);
        return newContent;
      });

      navigate("/office-screen");
    } catch (error) {
      console.error("Error fetching data for selected entries:", error);
    }
  };

  const buttonStyle = {
    backgroundColor: "rgba(227, 213, 202, 0.2)",
    color: "black",
    border: "none",
    borderRadius: "4px",
    padding: "10px 20px",
    cursor: "pointer",
    fontWeight: "bold",
    marginTop: "20px",
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
      }}
    >
      <div>
        <h1>View and Select Content</h1>
        <label htmlFor="table-select">Select Table:</label>
        <select
          id="table-select"
          value={selectedTable}
          onChange={handleTableChange}
        >
          <option value="">Select...</option>
          <option value="users">Users</option>
          <option value="comments">Comments</option>
          <option value="events">Events</option>
          <option value="canteenMenu">Canteen Menu</option>
          <option value="meetings">Meetings</option>
        </select>
        <div>
          <h2 style={{ marginBottom: "20px" }}>Content from {selectedTable}</h2>
          {tableData.length > 0 && (
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                textAlign: "left",
              }}
            >
              <thead>
                <tr>
                  <th>Select</th>
                  {Object.keys(tableData[0]).map((key) => (
                    <th key={key}>
                      {key.charAt(0).toUpperCase() + key.slice(1)}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {tableData.map((entry, index) => (
                  <tr
                    key={index}
                    style={{
                      backgroundColor:
                        index % 2 === 0
                          ? "rgba(227, 213, 202, 0.2)"
                          : "transparent",
                    }}
                  >
                    <td>
                      <input
                        type="checkbox"
                        id={`checkbox-${index}`}
                        name={`checkbox-${index}`}
                        checked={selectedEntries.includes(entry)}
                        onChange={() => handleCheckboxChange(entry)}
                      />
                    </td>
                    {Object.keys(tableData[0]).map((key) => (
                      <td key={key}>
                        {entry[key] !== undefined
                          ? key === "date" || key === "birthdate"
                            ? formatDate(entry[key])
                            : entry[key]
                          : "N/A"}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
        <button onClick={handleAddToScreen} style={buttonStyle}>
          Add to Screen
        </button>
      </div>
    </div>
  );
};

export default ViewContent;
