import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";
import DeleteFromDatabase from "./DeleteFromDatabase";
import screenfull from "screenfull";

const App = () => {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);
  const [showLinks, setShowLinks] = useState(true); // State to control link visibility

  useEffect(() => {
    const savedContent = localStorage.getItem("officeScreenContent");
    if (savedContent) {
      setOfficeScreenContent(JSON.parse(savedContent));
    }
  }, []);

  useEffect(() => {
    if (officeScreenContent.length !== 0) {
      localStorage.setItem(
        "officeScreenContent",
        JSON.stringify(officeScreenContent)
      );
    }
  }, [officeScreenContent]);

  const handleDeleteItems = (entries) => {
    const updatedContent = officeScreenContent.filter(
      (item) =>
        !entries.some(
          (entry) => item.id === entry.id && item.table === entry.table
        )
    );
    setOfficeScreenContent(updatedContent);
    localStorage.setItem("officeScreenContent", JSON.stringify(updatedContent));
  };

  const toggleLinkVisibility = () => {
    setShowLinks(!showLinks);
  };

  return (
    <Router>
      <div style={{ margin: 0, padding: 0 }}>
        <nav
          style={{
            backgroundColor: "rgba(227, 213, 202, 0.2)",
            padding: "10px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderBottom: "solid black 1px",
            width: "100%",
            boxSizing: "border-box",
            position: "fixed",
            top: 0,
            left: 0,
            zIndex: 1000,
          }}
        >
          <div style={{ fontSize: "40px", fontWeight: "bold", margin: 0 }}>
            Lightbeam
          </div>
          <ul
            style={{
              listStyleType: "none",
              display: "flex",
              gap: "10px",
              visibility: showLinks ? "visible" : "hidden",
              margin: 0,
              padding: 0,
            }}
          >
            <li style={{ margin: 0 }}>
              <Link to="/">Home</Link>
            </li>
            <li style={{ margin: 0 }}>
              <Link to="/add">Add Data</Link>
            </li>
            <li style={{ margin: 0 }}>
              <Link to="/view">View Content</Link>
            </li>
            <li style={{ margin: 0 }}>
              <Link to="/office-screen">Office Screen</Link>
            </li>
            <li style={{ margin: 0 }}>
              <Link to="/delete">Delete Data</Link>
            </li>
          </ul>
        </nav>

        <div style={{ paddingTop: "70px" }}>
          {" "}
          {/* Adjust padding to avoid content being hidden behind navbar */}
          <Routes>
            <Route
              path="/"
              element={
                <div>
                  <h1>Welcome to the Digital Signage System</h1>
                </div>
              }
            />
            <Route path="/add" element={<DynamicForm />} />
            <Route
              path="/view"
              element={
                <ViewContent setOfficeScreenContent={setOfficeScreenContent} />
              }
            />
            <Route
              path="/office-screen"
              element={
                <OfficeScreen
                  officeScreenContent={officeScreenContent}
                  handleDeleteItems={handleDeleteItems}
                  toggleLinkVisibility={toggleLinkVisibility} // Pass the toggle function
                />
              }
            />
            <Route path="/delete" element={<DeleteFromDatabase />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
