import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";

const App = () => {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);
  const initialContentRef = useRef(null); // Ref to store initial content

  useEffect(() => {
    const savedContent = localStorage.getItem("officeScreenContent");
    if (savedContent) {
      const parsedContent = JSON.parse(savedContent);
      setOfficeScreenContent(parsedContent);
      // Store initial content in the ref
      initialContentRef.current = parsedContent;
      console.log("Data loaded from localStorage:", parsedContent);
    } else {
      console.log("No data found in localStorage.");
    }
  }, []);

  useEffect(() => {
    // Check if officeScreenContent is different from the initial content
    if (
      JSON.stringify(officeScreenContent) !==
      JSON.stringify(initialContentRef.current)
    ) {
      console.log("Saving data to localStorage:", officeScreenContent);
      localStorage.setItem(
        "officeScreenContent",
        JSON.stringify(officeScreenContent)
      );
    }
  }, [officeScreenContent]);

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/add">Add Data</Link>
            </li>
            <li>
              <Link to="/view">View Content</Link>
            </li>
            <li>
              <Link to="/office-screen">Office Screen</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div>
                <h1>Welcome to the Digital Signage System</h1>
              </div>
            }
          />
          <Route path="/add" element={<DynamicForm />} />
          <Route
            path="/view"
            element={
              <ViewContent setOfficeScreenContent={setOfficeScreenContent} />
            }
          />
          <Route
            path="/office-screen"
            element={<OfficeScreen officeScreenContent={officeScreenContent} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
