import React, { useState, useEffect } from "react";
import screenfull from "screenfull";

const OfficeScreen = ({ officeScreenContent }) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
      };

      screenfull.on("change", changeHandler);

      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, []);

  // ... (your other functions formatDate, renderEntry, toggleSelection, removeSelectedItems remain the same)

  const toggleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setSelectedItems([]);
  };

  return (
    <div>
      {/* Button Container (only shown when NOT in fullscreen) */}
      {!screenfull.isFullscreen && (
        <div className="fullscreen-button-container">
          <button onClick={toggleFullscreen}>Enter Fullscreen</button>
          <button onClick={toggleEditMode}>
            {editMode ? "Cancel Edit" : "Edit"}
          </button>
        </div>
      )}

      <div
        className={`signage-layout ${
          screenfull.isFullscreen ? "fullscreen" : ""
        }`}
      >
        {/* Remove Button (only in edit mode and NOT in fullscreen) */}
        {editMode && !screenfull.isFullscreen && (
          <button onClick={removeSelectedItems}>Remove Selected</button>
        )}

        {/* Signage Content Sections */}
        <div className="signage-events">
          <h3>Events</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map(renderEntry)}
          </ul>
        </div>

        <div className="signage-meetings">
          <h3>Meetings</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map(renderEntry)}
          </ul>
        </div>

        <div className="signage-canteen-menu">
          <h3>Canteen Menu</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map(renderEntry)}
          </ul>
        </div>

        <div className="signage-other-content">
          <h3>Other Content</h3>
          <ul>
            {officeScreenContent
              .filter(
                (entry) =>
                  entry.table !== "events" &&
                  entry.table !== "meetings" &&
                  entry.table !== "canteenMenu"
              )
              .map(renderEntry)}
          </ul>
        </div>
      </div>

      {/* Your CSS in a <style> tag */}
      <style jsx>{`
        .fullscreen-button-container {
          /* Style your button container as needed */
        }

        .signage-layout {
          /* Style your main layout */
        }

        .signage-layout.fullscreen {
          /* Fullscreen-specific styles */
        }

        /* Styles for your content sections */
        .signage-events,
        .signage-meetings,
        .signage-canteen-menu,
        .signage-other-content {
          /* Style your content sections as needed */
        }
      `}</style>
    </div>
  );
};

export default OfficeScreen;
