// ViewContent.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ViewContent = () => {
  const [selectedTable, setSelectedTable] = useState("");
  const [tableData, setTableData] = useState([]);
  const [selectedEntries, setSelectedEntries] = useState({
    users: [],
    comments: [],
    events: [],
    canteenMenu: [],
    meetings: [],
  });
  const [selectedIds, setSelectedIds] = useState([]);
  const navigate = useNavigate();

  const fetchTableData = async (table) => {
    try {
      const response = await axios.get(`http://localhost:3000/${table}`);
      setTableData(response.data);
    } catch (error) {
      console.error(`Error fetching data from ${table}:`, error);
    }
  };

  const handleTableChange = (e) => {
    const table = e.target.value;
    setSelectedTable(table);
    if (table) {
      fetchTableData(table);
    } else {
      setTableData([]);
    }
    setSelectedIds([]);
  };

  const handleCheckboxChange = (id) => {
    setSelectedIds((prevSelectedIds) =>
      prevSelectedIds.includes(id)
        ? prevSelectedIds.filter((selectedId) => selectedId !== id)
        : [...prevSelectedIds, id]
    );
  };

  const handleAddToScreen = async () => {
    const entries = [];
    for (const id of selectedIds) {
      try {
        const response = await axios.get(
          `http://localhost:3000/${selectedTable}/${id}`
        );
        entries.push(response.data);
      } catch (error) {
        console.error(`Error fetching data for ID ${id}:`, error);
      }
    }
    const updatedEntries = {
      ...selectedEntries,
      [selectedTable]: entries,
    };
    setSelectedEntries(updatedEntries);
    navigate("/office-screen", { state: { selectedEntries: updatedEntries } });
  };

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry) => {
    return (
      <div>
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>View and Select Content</h1>
      <label>
        Select Table:
        <select value={selectedTable} onChange={handleTableChange}>
          <option value="">Select...</option>
          <option value="users">Users</option>
          <option value="comments">Comments</option>
          <option value="events">Events</option>
          <option value="canteenMenu">Canteen Menu</option>
          <option value="meetings">Meetings</option>
        </select>
      </label>
      <div>
        <h2>Content from {selectedTable}</h2>
        {tableData.length > 0 && (
          <ul>
            {tableData.map((entry) => (
              <li key={entry.id}>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(entry.id)}
                  onChange={() => handleCheckboxChange(entry.id)}
                />
                {renderEntry(entry)}
              </li>
            ))}
          </ul>
        )}
        <button onClick={handleAddToScreen}>Add to Screen</button>
      </div>
      <div>
        <h2>Selected Entries for Signage</h2>
        <div className="signage-layout">
          <div className="signage-events">
            <h3>Events</h3>
            <ul>
              {selectedEntries.events.map((event, index) => (
                <li key={index}>{renderEntry(event)}</li>
              ))}
            </ul>
          </div>
          <div className="signage-meetings">
            <h3>Meetings</h3>
            <ul>
              {selectedEntries.meetings.map((meeting, index) => (
                <li key={index}>{renderEntry(meeting)}</li>
              ))}
            </ul>
          </div>
          <div className="signage-canteen-menu">
            <h3>Canteen Menu</h3>
            <ul>
              {selectedEntries.canteenMenu.map((menu, index) => (
                <li key={index}>{renderEntry(menu)}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewContent;
