import React, { useState, useEffect } from "react";
import screenfull from "screenfull";

const OfficeScreen = ({ officeScreenContent }) => {
  const [showButton, setShowButton] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [checkedItems, setCheckedItems] = useState({});

  useEffect(() => {
    if (screenfull.isEnabled) {
      const changeHandler = () => {
        setShowButton(!screenfull.isFullscreen);
      };

      screenfull.on("change", changeHandler);

      return () => {
        screenfull.off("change", changeHandler);
      };
    }
  }, []);

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderEntry = (entry, index) => {
    return (
      <div key={index}>
        {editMode && (
          <input
            type="checkbox"
            checked={checkedItems[index] || false}
            onChange={() => toggleSelection(index)}
          />
        )}
        {Object.entries(entry).map(([key, value]) => (
          <p key={key}>
            <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{" "}
            {key === "date" || key === "birthdate" ? formatDate(value) : value}
          </p>
        ))}
      </div>
    );
  };

  const toggleFullscreen = () => {
    screenfull.toggle();
  };

  const toggleEditMode = () => {
    setEditMode(!editMode);
    setCheckedItems({}); // Clear selections when toggling edit mode
  };

  const toggleSelection = (index) => {
    setCheckedItems((prevChecked) => ({
      ...prevChecked,
      [index]: !prevChecked[index], // Toggle the checked state of the specific item
    }));
  };

  const removeSelectedItems = () => {
    const updatedContent = officeScreenContent.filter(
      (_, index) => !checkedItems[index] // Use checkedItems to filter
    );
    // Here, you would update your state/local storage with 'updatedContent'
    console.log("Items to remove:", selectedItems);
    console.log("Updated content:", updatedContent);

    setCheckedItems({});
    setEditMode(false);
  };

  return (
    <div>
      {showButton && (
        <div className="fullscreen-button-container">
          <button onClick={toggleFullscreen}>
            {screenfull.isFullscreen ? "Exit Fullscreen" : "Enter Fullscreen"}
          </button>
          {!screenfull.isFullscreen && (
            <button onClick={toggleEditMode}>
              {editMode ? "Cancel Edit" : "Edit"}
            </button>
          )}
        </div>
      )}

      <div
        className={`signage-layout ${
          screenfull.isFullscreen ? "fullscreen" : ""
        }`}
      >
        <div className="signage-events">
          <h3>Events</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "events")
              .map(renderEntry)}
          </ul>
        </div>

        <div className="signage-meetings">
          <h3>Meetings</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "meetings")
              .map(renderEntry)}
          </ul>
        </div>

        <div className="signage-canteen-menu">
          <h3>Canteen Menu</h3>
          <ul>
            {officeScreenContent
              .filter((entry) => entry.table === "canteenMenu")
              .map(renderEntry)}
          </ul>
        </div>

        <div className="signage-other-content">
          <h3>Other Content</h3>
          <ul>
            {officeScreenContent
              .filter(
                (entry) =>
                  entry.table !== "events" &&
                  entry.table !== "meetings" &&
                  entry.table !== "canteenMenu"
              )
              .map(renderEntry)}
          </ul>
        </div>

        {editMode && (
          <button onClick={removeSelectedItems}>Remove Selected</button>
        )}
      </div>

      <style jsx>{`
        /* Your CSS here */
        .signage-layout {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 20px;
        }

        .signage-layout.fullscreen {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 9999;
          background-color: black;
          grid-template-columns: 1fr;
        }

        /* Fullscreen button container styles */
        .fullscreen-button-container {
          position: absolute;
          top: 10px;
          right: 10px;
          z-index: 10000;
        }

        /* Hide the button container in fullscreen mode */
        .fullscreen-button-container.fullscreen {
          display: none;
        }

        /* Additional styles for the sections */
        .signage-events,
        .signage-meetings,
        .signage-canteen-menu,
        .signage-other-content {
          padding: 15px;
          border: 1px solid #ccc;
          border-radius: 5px;
        }
      `}</style>
    </div>
  );
};

export default OfficeScreen;
