import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import DynamicForm from "./DynamicForm";
import ViewContent from "./ViewContent";
import OfficeScreen from "./OfficeScreen";

const App = () => {
  const [officeScreenContent, setOfficeScreenContent] = useState([]);

  // Function to update officeScreenContent and save to localStorage
  const updateOfficeScreenContent = (newContent) => {
    setOfficeScreenContent((prevContent) => {
      const updatedContent = [...prevContent, ...newContent];

      // Save to localStorage only if the content has actually changed
      if (JSON.stringify(updatedContent) !== JSON.stringify(prevContent)) {
        localStorage.setItem(
          "officeScreenContent",
          JSON.stringify(updatedContent)
        );
      }

      return updatedContent;
    });
  };

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedContent = localStorage.getItem("officeScreenContent");
    if (savedContent) {
      setOfficeScreenContent(JSON.parse(savedContent));
      console.log("Data loaded from localStorage:", JSON.parse(savedContent));
    } else {
      console.log("No data found in localStorage.");
    }
  }, []);

  // Listen to beforeunload event to log final localStorage data
  useEffect(() => {
    const handleBeforeUnload = () => {
      console.log(
        "Final localStorage data before refresh:",
        localStorage.getItem("officeScreenContent")
      );
    };

    window.addEventListener("beforeunload", handleBeforeUnload);
    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []); // Empty dependency array ensures this runs only once

  return (
    <Router>
      {/* ... (your existing navigation) */}

      <Routes>
        {/* ... your other routes */}
        <Route
          path="/view"
          element={
            <ViewContent
              setOfficeScreenContent={updateOfficeScreenContent} // Pass the update function
            />
          }
        />
        <Route
          path="/office-screen"
          element={<OfficeScreen officeScreenContent={officeScreenContent} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
